01365     private List<IHtmlSyntaxNode> ParseCSharp(
01366         string cSharpText,
01367         int adhocTemplateOpeningLength,
01368         int offsetPositionIndex)
01369     {
01370         var injectedLanguageFragmentSyntaxes = new List<IHtmlSyntaxNode>();
01371 
01372         var lexer = new CSharpLexer(ResourceUri.Empty, cSharpText);
01373 
01374         lexer.Lex();
01375 
01376         foreach (var lexedTokenTextSpan in lexer.SyntaxTokenList.Select(x => x.TextSpan))
01377         {
01378             var startingIndexInclusive = lexedTokenTextSpan.StartingIndexInclusive +
01379                                          offsetPositionIndex -
01380                                          adhocTemplateOpeningLength;
01381 
01382             var endingIndexExclusive = lexedTokenTextSpan.EndingIndexExclusive +
01383                                        offsetPositionIndex -
01384                                        adhocTemplateOpeningLength;
01385 
01386             // startingIndexInclusive < 0 means it was part of the class template that was prepended so roslyn would recognize methods
01387             if (lexedTokenTextSpan.StartingIndexInclusive - adhocTemplateOpeningLength
01388                 < 0)
01389                 continue;
01390 
01391             var cSharpDecorationKind = (GenericDecorationKind)lexedTokenTextSpan.DecorationByte;
01392 
01393             switch (cSharpDecorationKind)
01394             {
01395                 case GenericDecorationKind.None:
01396                     break;
01397                 case GenericDecorationKind.Function:
01398                     {
01399                         var razorMethodTextSpan = lexedTokenTextSpan with
01400                         {
01401                             DecorationByte = (byte)HtmlDecorationKind.InjectedLanguageMethod,
01402                             StartingIndexInclusive = startingIndexInclusive,
01403                             EndingIndexExclusive = endingIndexExclusive,
01404                         };
01405 
01406                         injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentNode(
01407                             ImmutableArray<IHtmlSyntax>.Empty,
01408                             razorMethodTextSpan));
01409 
01410                         break;
01411                     }
01412                     // case CSharpDecorationKind.Type:
01413                     {
01414                         //     var razorTypeTextSpan = lexedTokenTextSpan with
01415                         //     {
01416                         //         DecorationByte = (byte)HtmlDecorationKind.InjectedLanguageType,
01417                         //         StartingIndexInclusive = startingIndexInclusive,
01418                         //         EndingIndexExclusive = endingIndexExclusive,
01419                         //     };
01420                         //
01421                         //     injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentSyntax(
01422                         //         ImmutableArray<IHtmlSyntax>.Empty,
01423                         //         string.Empty,
01424                         //         razorTypeTextSpan));
01425                         //
01426                         //     break;
01427                     }
01428                     // case CSharpDecorationKind.Parameter:
01429                     {
01430                         //     var razorVariableTextSpan = lexedTokenTextSpan with
01431                         //     {
01432                         //         DecorationByte = (byte)HtmlDecorationKind.InjectedLanguageVariable,
01433                         //         StartingIndexInclusive = startingIndexInclusive,
01434                         //         EndingIndexExclusive = endingIndexExclusive,
01435                         //     };
01436                         //
01437                         //     injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentSyntax(
01438                         //         ImmutableArray<IHtmlSyntax>.Empty,
01439                         //         string.Empty,
01440                         //         razorVariableTextSpan));
01441                         //
01442                         //     break;
01443                     }
01444                 case GenericDecorationKind.StringLiteral:
01445                     {
01446                         var razorStringLiteralTextSpan = lexedTokenTextSpan with
01447                         {
01448                             DecorationByte = (byte)HtmlDecorationKind.InjectedLanguageStringLiteral,
01449                             StartingIndexInclusive = startingIndexInclusive,
01450                             EndingIndexExclusive = endingIndexExclusive,
01451                         };
01452 
01453                         injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentNode(
01454                             ImmutableArray<IHtmlSyntax>.Empty,
01455                             razorStringLiteralTextSpan));
01456 
01457                         break;
01458                     }
01459                 case GenericDecorationKind.Keyword:
01460                     {
01461                         var razorKeywordTextSpan = lexedTokenTextSpan with
01462                         {
01463                             DecorationByte = (byte)HtmlDecorationKind.InjectedLanguageKeyword,
01464                             StartingIndexInclusive = startingIndexInclusive,
01465                             EndingIndexExclusive = endingIndexExclusive,
01466                         };
01467 
01468                         injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentNode(
01469                             ImmutableArray<IHtmlSyntax>.Empty,
01470                             razorKeywordTextSpan));
01471 
01472                         break;
01473                     }
01474                 case GenericDecorationKind.CommentSingleLine:
01475                     {
01476                         var razorCommentTextSpan = lexedTokenTextSpan with
01477                         {
01478                             DecorationByte = (byte)HtmlDecorationKind.Comment,
01479                             StartingIndexInclusive = startingIndexInclusive,
01480                             EndingIndexExclusive = endingIndexExclusive,
01481                         };
01482 
01483                         injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentNode(
01484                             ImmutableArray<IHtmlSyntax>.Empty,
01485                             razorCommentTextSpan));
01486 
01487                         break;
01488                     }
01489                 case GenericDecorationKind.CommentMultiLine:
01490                     {
01491                         var razorCommentTextSpan = lexedTokenTextSpan with
01492                         {
01493                             DecorationByte = (byte)HtmlDecorationKind.Comment,
01494                             StartingIndexInclusive = startingIndexInclusive,
01495                             EndingIndexExclusive = endingIndexExclusive,
01496                         };
01497 
01498                         injectedLanguageFragmentSyntaxes.Add(new InjectedLanguageFragmentNode(
01499                             ImmutableArray<IHtmlSyntax>.Empty,
01500                             razorCommentTextSpan));
01501 
01502                         break;
01503                     }
01504             }
01505         }
01506 
01507         return injectedLanguageFragmentSyntaxes;
01508     }
