00044 
00045         base.OnInitialized();
00046     }
00047 
00048     private async void DragStateWrapOnStateChanged(object? sender, EventArgs e)
00049     {
00050         if (!DragStateWrap.Value.ShouldDisplay)
00051         {
00052             var wasTargetOfDragging = _dragEventHandler is not null;
00053 
00054             _dragEventHandler = null;
00055             _previousDragMouseEventArgs = null;
00056 
00057             if (wasTargetOfDragging)
00058             {
00059                 Dispatcher.Dispatch(new AppDimensionState.NotifyIntraAppResizeAction());
00060             }
00061         }
00062         else
00063         {
00064             var mouseEventArgs = DragStateWrap.Value.MouseEventArgs;
00065 
00066             if (_dragEventHandler is not null)
00067             {
00068                 if (_previousDragMouseEventArgs is not null && mouseEventArgs is not null)
00069                 {
00070                     await _dragEventHandler
00071                         .Invoke((_previousDragMouseEventArgs, mouseEventArgs))
00072                         .ConfigureAwait(false);
00073                 }
00074 
00075                 _previousDragMouseEventArgs = mouseEventArgs;
00076                 await ReRenderFuncAsync.Invoke().ConfigureAwait(false);
00077             }
00078         }
00079     }
00080 
00081     private async Task SubscribeToDragEventAsync(
00082         Func<(MouseEventArgs firstMouseEventArgs, MouseEventArgs secondMouseEventArgs), Task> dragEventHandler)
00083     {
00084         _dragEventHandler = dragEventHandler;
00085 
00086         if (Drag is not null)
00087             await Drag.OnDragStartAsync().ConfigureAwait(false);
00088 
00089         Dispatcher.Dispatch(new DragState.ShouldDisplayAndMouseEventArgsAndDragSetAction(true, null, Drag));
00090     }
00091 
00092     public Task SubscribeToDragEventWithMoveHandle()
00093     {
00094         return SubscribeToDragEventAsync(DragEventHandlerMoveHandleAsync);
00095     }
