00100     {
00101         var parentElementWidth = ElementDimensions.DimensionAttributeList.Single(
00102             x => x.DimensionAttributeKind == DimensionAttributeKind.Width);
00103 
00104         _northResizeHandleDimensions.ElementPositionKind = ElementPositionKind.Absolute;
00105 
00106         // Width
00107         {
00108             var resizeHandleWidth = _northResizeHandleDimensions.DimensionAttributeList.Single(
00109                 x => x.DimensionAttributeKind == DimensionAttributeKind.Width);
00110 
00111             resizeHandleWidth.DimensionUnitList.Clear();
00112 
00113             resizeHandleWidth.DimensionUnitList.AddRange(parentElementWidth.DimensionUnitList);
00114 
00115             // width: calc(60vw - 42px);
00116             resizeHandleWidth.DimensionUnitList.Add(new DimensionUnit
00117             {
00118                 Value = RESIZE_HANDLE_SQUARE_PIXELS,
00119                 DimensionUnitKind = DimensionUnitKind.Pixels,
00120                 DimensionOperatorKind = DimensionOperatorKind.Subtract
00121             });
00122         }
