00008     public static void ResizeNorth(
00009         ElementDimensions elementDimensions,
00010         MouseEventArgs firstMouseEventArgs,
00011         MouseEventArgs secondMouseEventArgs)
00012     {
00013         var deltaY = secondMouseEventArgs.ClientY - firstMouseEventArgs.ClientY;
00014 
00015         // Height
00016         {
00017             var height = elementDimensions.DimensionAttributeList.Single(
00018                 da => da.DimensionAttributeKind == DimensionAttributeKind.Height);
00019 
00020             var heightOffsetInPixels = height.DimensionUnitList.FirstOrDefault(
00021                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
00022 
00023             if (heightOffsetInPixels is null)
00024             {
00025                 heightOffsetInPixels = new DimensionUnit
00026                 {
00027                     Value = 0,
00028                     DimensionOperatorKind = DimensionOperatorKind.Add,
00029                     DimensionUnitKind = DimensionUnitKind.Pixels
00030                 };
00031 
00032                 height.DimensionUnitList.Add(heightOffsetInPixels);
00033             }
00034 
00035             heightOffsetInPixels.Value -= deltaY;
00036         }
00037 
00038         // Top
00039         {
00040             var top = elementDimensions.DimensionAttributeList.Single(
00041                 da => da.DimensionAttributeKind == DimensionAttributeKind.Top);
00042 
00043             var topOffsetInPixels = top.DimensionUnitList.FirstOrDefault(
00044                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
00045 
00046             if (topOffsetInPixels is null)
00047             {
00048                 topOffsetInPixels = new DimensionUnit
00049                 {
00050                     Value = 0,
00051                     DimensionOperatorKind = DimensionOperatorKind.Add,
00052                     DimensionUnitKind = DimensionUnitKind.Pixels
00053                 };
00054 
00055                 top.DimensionUnitList.Add(topOffsetInPixels);
00056             }
00057 
00058             topOffsetInPixels.Value += deltaY;
00059         }
00060     }
