01060     private void InitializeResizeHelperTests(out ElementDimensions elementDimensions)
01061     {
01062         elementDimensions = DialogHelper.ConstructDefaultElementDimensions();
01063 
01064         // Width
01065         {
01066             var width = elementDimensions.DimensionAttributeList.Single(
01067                 da => da.DimensionAttributeKind == DimensionAttributeKind.Width);
01068 
01069             var widthOffsetInPixels = width.DimensionUnitList.FirstOrDefault(
01070                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
01071 
01072             if (widthOffsetInPixels is null)
01073             {
01074                 widthOffsetInPixels = new DimensionUnit
01075                 {
01076                     Value = 0,
01077                     DimensionOperatorKind = DimensionOperatorKind.Add,
01078                     DimensionUnitKind = DimensionUnitKind.Pixels
01079                 };
01080 
01081                 width.DimensionUnitList.Add(widthOffsetInPixels);
01082             }
01083         }
01084 
01085         // Height
01086         {
01087             var height = elementDimensions.DimensionAttributeList.Single(
01088                 da => da.DimensionAttributeKind == DimensionAttributeKind.Height);
01089 
01090             var heightOffsetInPixels = height.DimensionUnitList.FirstOrDefault(
01091                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
01092 
01093             if (heightOffsetInPixels is null)
01094             {
01095                 heightOffsetInPixels = new DimensionUnit
01096                 {
01097                     Value = 0,
01098                     DimensionOperatorKind = DimensionOperatorKind.Add,
01099                     DimensionUnitKind = DimensionUnitKind.Pixels
01100                 };
01101 
01102                 height.DimensionUnitList.Add(heightOffsetInPixels);
01103             }
01104         }
01105 
01106         // Left
01107         {
01108             var left = elementDimensions.DimensionAttributeList.Single(
01109                 da => da.DimensionAttributeKind == DimensionAttributeKind.Left);
01110 
01111             var leftOffsetInPixels = left.DimensionUnitList.FirstOrDefault(
01112                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
01113 
01114             if (leftOffsetInPixels is null)
01115             {
01116                 leftOffsetInPixels = new DimensionUnit
01117                 {
01118                     Value = 0,
01119                     DimensionOperatorKind = DimensionOperatorKind.Add,
01120                     DimensionUnitKind = DimensionUnitKind.Pixels
01121                 };
01122 
01123                 left.DimensionUnitList.Add(leftOffsetInPixels);
01124             }
01125         }
01126 
01127         // Top
01128         {
01129             var top = elementDimensions.DimensionAttributeList.Single(
01130                 da => da.DimensionAttributeKind == DimensionAttributeKind.Top);
01131 
01132             var topOffsetInPixels = top.DimensionUnitList.FirstOrDefault(
01133                 du => du.DimensionUnitKind == DimensionUnitKind.Pixels);
01134 
01135             if (topOffsetInPixels is null)
01136             {
01137                 topOffsetInPixels = new DimensionUnit
01138                 {
01139                     Value = 0,
01140                     DimensionOperatorKind = DimensionOperatorKind.Add,
01141                     DimensionUnitKind = DimensionUnitKind.Pixels
01142                 };
01143 
01144                 top.DimensionUnitList.Add(topOffsetInPixels);
01145             }
01146         }
01147     }
01148 
01149     public (double width, double height, double left, double top) GetPixelMeasurementsTuple(ElementDimensions elementDimensions)
01150     {
01151         var width = elementDimensions.DimensionAttributeList
01152             .Single(da => da.DimensionAttributeKind == DimensionAttributeKind.Width)
01153             .DimensionUnitList
01154             .First(du => du.DimensionUnitKind == DimensionUnitKind.Pixels)
01155             .Value;
01156 
01157         var height = elementDimensions.DimensionAttributeList
01158             .Single(da => da.DimensionAttributeKind == DimensionAttributeKind.Height)
01159             .DimensionUnitList
01160             .First(du => du.DimensionUnitKind == DimensionUnitKind.Pixels)
01161             .Value;
01162 
01163         var left = elementDimensions.DimensionAttributeList
01164             .Single(da => da.DimensionAttributeKind == DimensionAttributeKind.Left)
01165             .DimensionUnitList
01166             .First(du => du.DimensionUnitKind == DimensionUnitKind.Pixels)
01167             .Value;
01168 
01169         var top = elementDimensions.DimensionAttributeList
01170             .Single(da => da.DimensionAttributeKind == DimensionAttributeKind.Top)
01171             .DimensionUnitList
01172             .First(du => du.DimensionUnitKind == DimensionUnitKind.Pixels)
01173             .Value;
01174 
01175         return (width, height, left, top);
01176     }
