00026     public static void LoadDotNet6(CSharpBinder cSharpBinder)
00027     {
00028         if (_dotNet6Loader is null)
00029         {
00030             lock (ConstructRuntimeAssembliesLoaderLock)
00031             {
00032                 if (_dotNet6Loader is null)
00033                 {
00034                     _dotNet6Loader = new RuntimeAssembliesLoader();
00035                 }
00036             }    
00037         }
00038 
00039         try
00040         {
00041             // TODO: This completely defeats the purpose of the cache. At some point this might be improved by re-using the previous calculation. (2023-08-06)
00042             _dotNet6Loader.CreateCache(cSharpBinder);
00043         }
00044         catch (System.IO.FileNotFoundException)
00045         {
00046             // WASM hosting of the application results in a 'System.IO.FileNotFoundException' because
00047             // "The loading of Unhandled exception rendering component: Could not find core assembly. Either specify a valid core assembly name in the MetadataLoadContext constructor or provide a MetadataAssemblyResolver that can load the core assembly."
00048             //
00049             // TODO: Fix this issue instead of swallowing the exception.
00050         }
00051     }
