00055         public void CreateCache(CSharpBinder cSharpBinder)
00056         {
00057             var globalCodeBlockBuilder = new CodeBlockBuilder(null, null);
00058             var currentCodeBlockBuilder = globalCodeBlockBuilder;
00059             var diagnosticBag = new DiagnosticBag();
00060 
00061             var model = new CSharpParserModel(
00062                 cSharpBinder,
00063                 (CSharpBinderSession)cSharpBinder.StartBinderSession(new ResourceUri("aaa")),
00064                 new TokenWalker(ImmutableArray<ISyntaxToken>.Empty, new()),
00065                 new Stack<ISyntax>(),
00066                 diagnosticBag,
00067                 globalCodeBlockBuilder,
00068                 currentCodeBlockBuilder);
00069 
00070             // TODO: Do not use 'System.IO.Directory' because this doesn't work...
00071             // ... when running the website
00072             //
00073             // Get the array of runtime assemblies.
00074             string[] runtimeAssemblyPaths = Directory.GetFiles(
00075                 RuntimeEnvironment.GetRuntimeDirectory(),
00076                 "*.dll");
00077 
00078             // Create the list of assembly paths consisting of runtime assemblies.
00079             var listOfRuntimeAssemblyPaths = new List<string>(runtimeAssemblyPaths);
00080 
00081             // Create PathAssemblyResolver that can resolve assemblies using the created list.
00082             var resolver = new PathAssemblyResolver(listOfRuntimeAssemblyPaths);
00083             
00084             var metadataLoadContext = new MetadataLoadContext(resolver);
00085 
00086             using (metadataLoadContext)
00087             {
00088                 // TODO: Don't target System.Console only. This 'Where' should be removed.
00089                 runtimeAssemblyPaths = runtimeAssemblyPaths
00090                     .Where(a => a.Contains("Console"))
00091                     .ToArray();
00092                 
00093                 // Load runtime assemblies into MetadataLoadContext.
00094                 foreach (var runtimeAssemblyPath in runtimeAssemblyPaths)
00095                 {
00096                     Assembly assembly = metadataLoadContext.LoadFromAssemblyPath(runtimeAssemblyPath);
00097                 
00098                     AssemblyName name = assembly.GetName();
00099 
00100                     var consoleType = assembly.DefinedTypes
00101                         .Where(dt => dt.Name == "Console");
00102                     
00103                     // TODO: do a foreach over 'assembly.DefinedTypes' instead of singling out Console
00104                     foreach (var definedType in consoleType)
00105                     {
00106                         try
00107                         {
00108                             var textSpan = TextEditorTextSpan.FabricateTextSpan(
00109                                 definedType.Name);
00110 
00111                             var typeDefinitionNode = new TypeDefinitionNode(
00112                                 AccessModifierKind.Public,
00113                                 false,
00114                                 StorageModifierKind.Class,
00115                                 new IdentifierToken(textSpan),
00116                                 null,
00117                                 null,
00118                                 null,
00119                                 null,
00120                                 openBraceToken: default,
00121                                 null);
00122 
00123                             var declaredMethods = definedType.GetMethods();
00124 
00125                             var typeBodyCodeBlockNodeBuilder = new CodeBlockBuilder(null, typeDefinitionNode);
00126 
00127                             foreach (var method in declaredMethods)
00128                             {
00129                                 // Return Type
00130                                 var returnTypeTextSpan = TextEditorTextSpan.FabricateTextSpan(method.ReturnType.Name);
00131                                 var returnTypeClauseNode = new TypeClauseNode(new IdentifierToken(returnTypeTextSpan), null, null);
00132 
00133                                 // Identifier
00134                                 var functionIdentifierTextSpan = TextEditorTextSpan.FabricateTextSpan(method.Name);
00135                                 var functionIdentifierToken = new IdentifierToken(functionIdentifierTextSpan);
00136 
00137                                 // Function Arguments Open Parenthesis
00138                                 var openParenthesisTextSpan = TextEditorTextSpan.FabricateTextSpan("(");
00139                                 var openParenthesisToken = new OpenParenthesisToken(openParenthesisTextSpan);
00140 
00141                                 // Function Argument Entries
00142                                 List<FunctionArgumentEntryNode>? functionArgumentEntryNodeList = new();
00143 
00144                                 var functionArguments = method.GetParameters();
00145 
00146                                 if (functionArguments.Any())
00147                                 {
00148                                     foreach (var functionArgument in functionArguments)
00149                                     {
00150                                         var typeClauseNode = GetTypeClauseNode(
00151                                             functionArgument.ParameterType.Name,
00152                                             functionArgument.ParameterType.GenericTypeArguments);
00153 
00154                                         var argumentTextSpan = TextEditorTextSpan.FabricateTextSpan(functionArgument.Name ?? "luthetus_error_null");
00155                                         var argumentIdentifierToken = new IdentifierToken(argumentTextSpan);
00156 
00157                                         var variableDeclarationStatementNode = new VariableDeclarationNode(
00158                                             typeClauseNode,
00159                                             argumentIdentifierToken,
00160                                             VariableKind.Local,
00161                                             false);
00162 
00163                                         var functionArgumentEntryNode = new FunctionArgumentEntryNode(
00164                                             variableDeclarationStatementNode,
00165                                             optionalCompileTimeConstantToken: null,
00166                                             false,
00167                                             false,
00168                                             false,
00169                                             false,
00170                                             false);
00171 
00172                                         functionArgumentEntryNodeList.Add(functionArgumentEntryNode);
00173                                     }
00174                                 }
00175 
00176                                 // Function Arguments Close Parenthesis
00177                                 var closeParenthesisTextSpan = TextEditorTextSpan.FabricateTextSpan(")");
00178                                 var closeParenthesisToken = new CloseParenthesisToken(closeParenthesisTextSpan);
00179 
00180                                 // Function Arguments Listing
00181                                 var functionArgumentsListingNode = new FunctionArgumentsListingNode(
00182                                     openParenthesisToken,
00183                                     functionArgumentEntryNodeList,
00184                                     closeParenthesisToken);
00185 
00186                                 GenericArgumentsListingNode? genericArgumentsListingNode = null;
00187 
00188                                 var genericArguments = method.GetGenericArguments();
00189 
00190                                 if (genericArguments.Any())
00191                                 {
00192                                     // Generic Arguments Open Angle Bracket
00193                                     var openAngleBracketTextSpan = TextEditorTextSpan.FabricateTextSpan("<");
00194                                     var openAngleBracketToken = new OpenAngleBracketToken(openAngleBracketTextSpan);
00195 
00196                                     // Generic Argument Entries
00197                                     var genericArgumentEntryNodeList = new List<GenericArgumentEntryNode>();
00198 
00199                                     foreach (var genericArgument in genericArguments)
00200                                     {
00201                                         var typeClauseNode = GetTypeClauseNode(
00202                                             genericArgument.Name,
00203                                             genericArgument.GenericTypeArguments);
00204 
00205                                         var genericArgumentEntryNode = new GenericArgumentEntryNode(
00206                                             typeClauseNode);
00207 
00208                                         genericArgumentEntryNodeList.Add(genericArgumentEntryNode);
00209                                     }
00210 
00211                                     // Generic Arguments Close Parenthesis
00212                                     var closeAngleBracketTextSpan = TextEditorTextSpan.FabricateTextSpan(">");
00213                                     var closeAngleBracketToken = new CloseAngleBracketToken(closeAngleBracketTextSpan);
00214 
00215                                     // Generic Arguments Listing
00216                                     genericArgumentsListingNode = new GenericArgumentsListingNode(
00217                                         openAngleBracketToken,
00218                                         genericArgumentEntryNodeList.ToImmutableArray(),
00219                                         closeAngleBracketToken);
00220                                 }
00221 
00222                                 var functionDefinitionNode = new FunctionDefinitionNode(
00223                                     AccessModifierKind.Public,
00224                                     returnTypeClauseNode,
00225                                     functionIdentifierToken,
00226                                     genericArgumentsListingNode: null,
00227                                     functionArgumentsListingNode,
00228                                     null,
00229                                     null);
00230 
00231                                 typeBodyCodeBlockNodeBuilder.ChildList.Add(functionDefinitionNode);
00232                             }
00233 
00234                             typeDefinitionNode = new TypeDefinitionNode(
00235                                 AccessModifierKind.Public,
00236                                 typeDefinitionNode.HasPartialModifier,
00237                                 StorageModifierKind.Class,
00238                                 typeDefinitionNode.TypeIdentifierToken,
00239                                 null,
00240                                 typeDefinitionNode.GenericArgumentsListingNode,
00241                                 null,
00242                                 typeDefinitionNode.InheritedTypeClauseNode,
00243                                 openBraceToken: default,
00244                                 typeBodyCodeBlockNodeBuilder.Build());
00245 
00246                             var typeNamespace = definedType.Namespace;
00247 
00248                             var systemNamespaceGroup = cSharpBinder.NamespaceGroupNodes["System"];
00249 
00250                             foreach (var namespaceStatementNode in systemNamespaceGroup.NamespaceStatementNodeList)
00251                             {
00252                                 cSharpBinder.BindNamespaceStatementNode(namespaceStatementNode, model);
00253                             }
00254                         }
00255                         catch (FileNotFoundException ex)
00256                         {
00257                             // We are missing the required dependency assembly.
00258                             Console.WriteLine($"Error while getting attribute type: {ex.Message}");
00259                         }
00260                     }
00261                 }
00262             }
00263         }
