00265         private TypeClauseNode GetTypeClauseNode(
00266             string name,
00267             Type[] genericParameters)
00268         {
00269             var typeTextSpan = TextEditorTextSpan.FabricateTextSpan(name);
00270             var typeIdentifierToken = new IdentifierToken(typeTextSpan);
00271 
00272             GenericParametersListingNode? genericParametersListingNode = null;
00273 
00274             if (genericParameters.Any())
00275             {
00276                 // Generic Parameters Open Angle Bracket
00277                 var openAngleBracketTextSpan = TextEditorTextSpan.FabricateTextSpan("<");
00278                 var openAngleBracketToken = new OpenAngleBracketToken(openAngleBracketTextSpan);
00279 
00280                 // Generic Parameter Entries
00281                 var genericParameterEntryNodeList = new List<GenericParameterEntryNode>();
00282 
00283                 foreach (var genericParameter in genericParameters)
00284                 {
00285                     var genericParameterEntryNode = new GenericParameterEntryNode(
00286                         GetTypeClauseNode(
00287                             genericParameter.Name,
00288                             genericParameter.GenericTypeArguments));
00289 
00290                     genericParameterEntryNodeList.Add(genericParameterEntryNode);
00291                 }
00292 
00293                 // Generic Parameters Close Parenthesis
00294                 var closeAngleBracketTextSpan = TextEditorTextSpan.FabricateTextSpan(">");
00295                 var closeAngleBracketToken = new CloseAngleBracketToken(closeAngleBracketTextSpan);
00296 
00297                 // Generic Parameters Listing
00298                 genericParametersListingNode = new GenericParametersListingNode(
00299                     openAngleBracketToken,
00300                     genericParameterEntryNodeList,
00301                     closeAngleBracketToken);
00302             }
00303 
00304             return new TypeClauseNode(
00305                 typeIdentifierToken,
00306                 null,
00307                 genericParametersListingNode);
00308         }
