00059     private string HORIZONTAL_GetScrollbarHorizontalStyleCss()
00060     {
00061         var renderBatchLocal = RenderBatch;
00062         if (renderBatchLocal is null)
00063             return string.Empty;
00064         
00065         var scrollbarWidthInPixels = renderBatchLocal.ViewModel.TextEditorDimensions.Width -
00066             ScrollbarFacts.SCROLLBAR_SIZE_IN_PIXELS;
00067 
00068         var scrollbarWidthInPixelsInvariantCulture = scrollbarWidthInPixels.ToCssValue();
00069         var width = $"width: {scrollbarWidthInPixelsInvariantCulture}px;";
00070 
00071         return width;
00072     }
