00074     private string HORIZONTAL_GetSliderHorizontalStyleCss()
00075     {
00076         var renderBatchLocal = RenderBatch;
00077         if (renderBatchLocal is null)
00078             return string.Empty;
00079         
00080         var scrollbarWidthInPixels = renderBatchLocal.ViewModel.TextEditorDimensions.Width -
00081             ScrollbarFacts.SCROLLBAR_SIZE_IN_PIXELS;
00082 
00083         // Proportional Left
00084         var sliderProportionalLeftInPixels = renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollLeft *
00085             scrollbarWidthInPixels /
00086             renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollWidth;
00087 
00088         var sliderProportionalLeftInPixelsInvariantCulture = sliderProportionalLeftInPixels.ToCssValue();
00089         var left = $"left: {sliderProportionalLeftInPixelsInvariantCulture}px;";
00090 
00091         // Proportional Width
00092         var pageWidth = renderBatchLocal.ViewModel.TextEditorDimensions.Width;
00093 
00094         var sliderProportionalWidthInPixels = pageWidth *
00095             scrollbarWidthInPixels /
00096             renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollWidth;
00097 
00098         var sliderProportionalWidthInPixelsInvariantCulture = sliderProportionalWidthInPixels.ToCssValue();
00099         var width = $"width: {sliderProportionalWidthInPixelsInvariantCulture}px;";
00100 
00101         return $"{left} {width}";
00102     }
