00138     private async Task HORIZONTAL_HandleOnMouseDownAsync(MouseEventArgs mouseEventArgs)
00139     {
00140         var renderBatchLocal = RenderBatch;
00141         if (renderBatchLocal is null)
00142             return;
00143         
00144         HORIZONTAL_thinksLeftMouseButtonIsDown = true;
00145         HORIZONTAL_scrollLeftOnMouseDown = renderBatchLocal.ViewModel.ScrollbarDimensions.ScrollLeft;
00146 
00147         var scrollbarBoundingClientRect = await TextEditorService.JsRuntimeCommonApi
00148             .MeasureElementById(HORIZONTAL_ScrollbarElementId)
00149             .ConfigureAwait(false);
00150 
00151         // Drag far up to reset scroll to original
00152         var textEditorDimensions = renderBatchLocal.ViewModel.TextEditorDimensions;
00153         var distanceBetweenTopEditorAndTopScrollbar = scrollbarBoundingClientRect.TopInPixels - textEditorDimensions.BoundingClientRectTop;
00154         HORIZONTAL_clientYThresholdToResetScrollLeftPosition = scrollbarBoundingClientRect.TopInPixels - DISTANCE_TO_RESET_SCROLL_POSITION;
00155 
00156         // Subscribe to the drag events
00157         //
00158         // NOTE: '_mouseDownEventArgs' being non-null is what indicates that the subscription is active.
00159         //       So be wary if one intends to move its assignment elsewhere.
00160         {
00161             _mouseDownEventArgs = mouseEventArgs;
00162             _dragEventHandler = HORIZONTAL_DragEventHandlerScrollAsync;
00163     
00164             Dispatcher.Dispatch(new DragState.ShouldDisplayAndMouseEventArgsSetAction(true, null));
00165         }
00166     }
