00198     private async void DragStateWrapOnStateChanged(object? sender, EventArgs e)
00199     {
00200         if (!DragStateWrap.Value.ShouldDisplay)
00201         {
00202             // NOTE: '_mouseDownEventArgs' being non-null is what indicates that the subscription is active.
00203             //       So be wary if one intends to move its assignment elsewhere.
00204             _mouseDownEventArgs = null;
00205         }
00206         else
00207         {
00208             var localMouseDownEventArgs = _mouseDownEventArgs;
00209             var dragEventArgs = DragStateWrap.Value.MouseEventArgs;
00210             var localDragEventHandler = _dragEventHandler;
00211 
00212             if (localMouseDownEventArgs is not null && dragEventArgs is not null)
00213                 await localDragEventHandler.Invoke(localMouseDownEventArgs, dragEventArgs).ConfigureAwait(false);
00214         }
00215     }
