00217     private Task HORIZONTAL_DragEventHandlerScrollAsync(MouseEventArgs localMouseDownEventArgs, MouseEventArgs onDragMouseEventArgs)
00218     {
00219         var renderBatchLocal = RenderBatch;
00220         if (renderBatchLocal is null)
00221             return Task.CompletedTask;
00222         
00223         var localThinksLeftMouseButtonIsDown = HORIZONTAL_thinksLeftMouseButtonIsDown;
00224 
00225         if (!localThinksLeftMouseButtonIsDown)
00226             return Task.CompletedTask;
00227 
00228         // Buttons is a bit flag '& 1' gets if left mouse button is held
00229         if (localThinksLeftMouseButtonIsDown && (onDragMouseEventArgs.Buttons & 1) == 1)
00230         {
00231             var textEditorDimensions = renderBatchLocal.ViewModel.TextEditorDimensions;
00232             var scrollbarDimensions = renderBatchLocal.ViewModel.ScrollbarDimensions;
00233         
00234             OnScrollHorizontal onScrollHorizontal;
00235 
00236             if (onDragMouseEventArgs.ClientY < HORIZONTAL_clientYThresholdToResetScrollLeftPosition)
00237             {
00238                 // Drag far left to reset scroll to original
00239                 onScrollHorizontal = new OnScrollHorizontal(
00240                     HORIZONTAL_scrollLeftOnMouseDown,
00241                     renderBatchLocal.ComponentData,
00242                     renderBatchLocal.ViewModel.ViewModelKey);
00243             }
00244             else
00245             {
00246                 var diffX = onDragMouseEventArgs.ClientX - localMouseDownEventArgs.ClientX;
00247     
00248                 var scrollbarWidthInPixels = textEditorDimensions.Width - ScrollbarFacts.SCROLLBAR_SIZE_IN_PIXELS;
00249     
00250                 var scrollLeft = HORIZONTAL_scrollLeftOnMouseDown +
00251                     diffX *
00252                     scrollbarDimensions.ScrollWidth /
00253                     scrollbarWidthInPixels;
00254     
00255                 if (scrollLeft + textEditorDimensions.Width > scrollbarDimensions.ScrollWidth)
00256                     scrollLeft = scrollbarDimensions.ScrollWidth - textEditorDimensions.Width;
00257 
00258                 if (scrollLeft < 0)
00259                     scrollLeft = 0;
00260     
00261                 onScrollHorizontal = new OnScrollHorizontal(
00262                     scrollLeft,
00263                     renderBatchLocal.ComponentData,
00264                     renderBatchLocal.ViewModel.ViewModelKey);
00265             }
00266 
00267             TextEditorService.Post(onScrollHorizontal);
00268         }
00269         else
00270         {
00271             HORIZONTAL_thinksLeftMouseButtonIsDown = false;
00272         }
00273 
00274         return Task.CompletedTask;
00275     }
