00091     private MenuRecord GetMenuRecord(TreeViewCommandArgs commandArgs)
00092     {
00093         if (_previousGetMenuRecordInvocation.treeViewCommandArgs == commandArgs)
00094             return _previousGetMenuRecordInvocation.menuRecord;
00095 
00096         if (commandArgs.TreeViewContainer.SelectedNodeList.Count > 1)
00097             return GetMenuRecordManySelections(commandArgs);
00098 
00099         if (commandArgs.TreeViewContainer.ActiveNode is null)
00100         {
00101             var menuRecord = MenuRecord.Empty;
00102             _previousGetMenuRecordInvocation = (commandArgs, menuRecord);
00103             return menuRecord;
00104         }
00105 
00106         var menuOptionList = new List<MenuOptionRecord>();
00107         var treeViewModel = commandArgs.TreeViewContainer.ActiveNode;
00108         var parentTreeViewModel = treeViewModel.Parent;
00109         var parentTreeViewNamespacePath = parentTreeViewModel as TreeViewNamespacePath;
00110 
00111         if (treeViewModel is TreeViewNamespacePath treeViewNamespacePath)
00112         {
00113             if (treeViewNamespacePath.Item.AbsolutePath.IsDirectory)
00114             {
00115                 menuOptionList.AddRange(GetFileMenuOptions(treeViewNamespacePath, parentTreeViewNamespacePath)
00116                     .Union(GetDirectoryMenuOptions(treeViewNamespacePath))
00117                     .Union(GetDebugMenuOptions(treeViewNamespacePath)));
00118             }
00119             else
00120             {
00121                 switch (treeViewNamespacePath.Item.AbsolutePath.ExtensionNoPeriod)
00122                 {
00123                     case ExtensionNoPeriodFacts.C_SHARP_PROJECT:
00124                         menuOptionList.AddRange(GetCSharpProjectMenuOptions(treeViewNamespacePath)
00125                             .Union(GetDebugMenuOptions(treeViewNamespacePath)));
00126                         break;
00127                     default:
00128                         menuOptionList.AddRange(GetFileMenuOptions(treeViewNamespacePath, parentTreeViewNamespacePath)
00129                             .Union(GetDebugMenuOptions(treeViewNamespacePath)));
00130                         break;
00131                 }
00132             }
00133         }
00134         else if (treeViewModel is TreeViewSolution treeViewSolution)
00135         {
00136             if (ExtensionNoPeriodFacts.DOT_NET_SOLUTION == treeViewSolution.Item.NamespacePath.AbsolutePath.ExtensionNoPeriod)
00137             {
00138                 if (treeViewSolution.Parent is null || treeViewSolution.Parent is TreeViewAdhoc)
00139                     menuOptionList.AddRange(GetDotNetSolutionMenuOptions(treeViewSolution));
00140             }
00141         }
00142         else if (treeViewModel is TreeViewCSharpProjectToProjectReference treeViewCSharpProjectToProjectReference)
00143         {
00144             menuOptionList.AddRange(GetCSharpProjectToProjectReferenceMenuOptions(
00145                 treeViewCSharpProjectToProjectReference));
00146         }
00147         else if (treeViewModel is TreeViewCSharpProjectNugetPackageReference treeViewCSharpProjectNugetPackageReference)
00148         {
00149             menuOptionList.AddRange(GetTreeViewLightWeightNugetPackageRecordMenuOptions(
00150                 treeViewCSharpProjectNugetPackageReference));
00151         }
00152 
00153         if (!menuOptionList.Any())
00154         {
00155             var menuRecord = MenuRecord.Empty;
00156             _previousGetMenuRecordInvocation = (commandArgs, menuRecord);
00157             return menuRecord;
00158         }
00159 
00160         // Default case
00161         {
00162             var menuRecord = new MenuRecord(menuOptionList.ToImmutableArray());
00163             _previousGetMenuRecordInvocation = (commandArgs, menuRecord);
00164             return menuRecord;
00165         }
00166     }
