00168     private MenuRecord GetMenuRecordManySelections(TreeViewCommandArgs commandArgs)
00169     {
00170         var menuOptionList = new List<MenuOptionRecord>();
00171 
00172         var getFileOptions = true;
00173         var filenameList = new List<string>();
00174 
00175         foreach (var selectedNode in commandArgs.TreeViewContainer.SelectedNodeList)
00176         {
00177             if (selectedNode is TreeViewNamespacePath treeViewNamespacePath)
00178             {
00179                 if (treeViewNamespacePath.Item.AbsolutePath.ExtensionNoPeriod == ExtensionNoPeriodFacts.C_SHARP_PROJECT)
00180                     getFileOptions = false;
00181                 else if (getFileOptions)
00182                     filenameList.Add(treeViewNamespacePath.Item.AbsolutePath.NameWithExtension + " __FROM__ " + (treeViewNamespacePath.Item.AbsolutePath.ParentDirectory?.Value ?? "null"));
00183             }
00184             else
00185             {
00186                 getFileOptions = false;
00187             }
00188         }
00189 
00190         if (getFileOptions)
00191         {
00192             menuOptionList.Add(new MenuOptionRecord(
00193                 "Delete",
00194                 MenuOptionKind.Delete,
00195                 WidgetRendererType: IdeComponentRenderers.BooleanPromptOrCancelRendererType,
00196                 WidgetParameterMap: new Dictionary<string, object?>
00197                 {
00198                     { nameof(IBooleanPromptOrCancelRendererType.IncludeCancelOption), false },
00199                     { nameof(IBooleanPromptOrCancelRendererType.Message), $"DELETE:" },
00200                     { nameof(BooleanPromptOrCancelDisplay.ListOfMessages), filenameList },
00201                     { nameof(IBooleanPromptOrCancelRendererType.AcceptOptionTextOverride), null },
00202                     { nameof(IBooleanPromptOrCancelRendererType.DeclineOptionTextOverride), null },
00203                     {
00204                         nameof(IBooleanPromptOrCancelRendererType.OnAfterAcceptFunc),
00205                         async () =>
00206                         {
00207                             await commandArgs.RestoreFocusToTreeView
00208                                 .Invoke()
00209                                 .ConfigureAwait(false);
00210 
00211                             BackgroundTaskService.Enqueue(
00212                                 Key<IBackgroundTask>.NewKey(),
00213                                 ContinuousBackgroundTaskWorker.GetQueueKey(),
00214                                 "SolutionExplorer_TreeView_MultiSelect_DeleteFiles",
00215                                 async () =>
00216                                 {
00217                                     foreach (var node in commandArgs.TreeViewContainer.SelectedNodeList)
00218                                     {
00219                                         var treeViewNamespacePath = (TreeViewNamespacePath)node;
00220 
00221                                         if (treeViewNamespacePath.Item.AbsolutePath.IsDirectory)
00222                                         {
00223                                             await FileSystemProvider.Directory
00224                                                 .DeleteAsync(treeViewNamespacePath.Item.AbsolutePath.Value, true, CancellationToken.None)
00225                                                 .ConfigureAwait(false);
00226                                         }
00227                                         else
00228                                         {
00229                                             await FileSystemProvider.File
00230                                                 .DeleteAsync(treeViewNamespacePath.Item.AbsolutePath.Value)
00231                                                 .ConfigureAwait(false);
00232                                         }
00233 
00234                                         if (TreeViewService.TryGetTreeViewContainer(commandArgs.TreeViewContainer.Key, out var mostRecentContainer) &&
00235                                             mostRecentContainer is not null)
00236                                         {
00237                                             var localParent = node.Parent;
00238 
00239                                             if (localParent is not null)
00240                                             {
00241                                                 await localParent.LoadChildListAsync().ConfigureAwait(false);
00242                                                 TreeViewService.ReRenderNode(mostRecentContainer.Key, localParent);
00243                                             }
00244                                         }
00245                                     }
00246                                 });
00247                         }
00248                     },
00249                     { nameof(IBooleanPromptOrCancelRendererType.OnAfterDeclineFunc), commandArgs.RestoreFocusToTreeView },
00250                     { nameof(IBooleanPromptOrCancelRendererType.OnAfterCancelFunc), commandArgs.RestoreFocusToTreeView },
00251                 }));
00252         }
00253 
00254         if (!menuOptionList.Any())
00255         {
00256             var menuRecord = MenuRecord.Empty;
00257             _previousGetMenuRecordInvocation = (commandArgs, menuRecord);
00258             return menuRecord;
00259         }
00260 
00261         // Default case
00262         {
00263             var menuRecord = new MenuRecord(menuOptionList.ToImmutableArray());
00264             _previousGetMenuRecordInvocation = (commandArgs, menuRecord);
00265             return menuRecord;
00266         }
00267     }
