00306     private MenuOptionRecord[] GetCSharpProjectMenuOptions(TreeViewNamespacePath treeViewModel)
00307     {
00308         var parentDirectory = treeViewModel.Item.AbsolutePath.AncestorDirectoryList.Last();
00309         var treeViewSolution = treeViewModel.Parent as TreeViewSolution;
00310 
00311         if (treeViewSolution is null)
00312         {
00313             var ancestorTreeView = treeViewModel.Parent;
00314 
00315             if (ancestorTreeView?.Parent is null)
00316                 return Array.Empty<MenuOptionRecord>();
00317 
00318             // Parent could be a could be one or many levels of solution folders
00319             while (ancestorTreeView.Parent is not null)
00320             {
00321                 ancestorTreeView = ancestorTreeView.Parent;
00322             }
00323 
00324             treeViewSolution = ancestorTreeView as TreeViewSolution;
00325 
00326             if (treeViewSolution is null)
00327                 return Array.Empty<MenuOptionRecord>();
00328         }
00329 
00330         var parentDirectoryAbsolutePath = EnvironmentProvider.AbsolutePathFactory(parentDirectory.Value, true);
00331 
00332         return new[]
00333         {
00334             MenuOptionsFactory.NewEmptyFile(
00335                 parentDirectoryAbsolutePath,
00336                 async () => await ReloadTreeViewModel(treeViewModel).ConfigureAwait(false)),
00337             MenuOptionsFactory.NewTemplatedFile(
00338                 new NamespacePath(treeViewModel.Item.Namespace, parentDirectoryAbsolutePath),
00339                 async () => await ReloadTreeViewModel(treeViewModel).ConfigureAwait(false)),
00340             MenuOptionsFactory.NewDirectory(
00341                 parentDirectoryAbsolutePath,
00342                 async () => await ReloadTreeViewModel(treeViewModel).ConfigureAwait(false)),
00343             MenuOptionsFactory.PasteClipboard(
00344                 parentDirectoryAbsolutePath,
00345                 async () =>
00346                 {
00347                     var localParentOfCutFile = ParentOfCutFile;
00348                     ParentOfCutFile = null;
00349 
00350                     if (localParentOfCutFile is not null)
00351                         await ReloadTreeViewModel(localParentOfCutFile).ConfigureAwait(false);
00352 
00353                     await ReloadTreeViewModel(treeViewModel).ConfigureAwait(false);
00354                 }),
00355             DotNetMenuOptionsFactory.AddProjectToProjectReference(
00356                 treeViewModel,
00357                 TerminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY],
00358                 Dispatcher,
00359                 IdeBackgroundTaskApi,
00360                 () => Task.CompletedTask),
00361             DotNetMenuOptionsFactory.MoveProjectToSolutionFolder(
00362                 treeViewSolution,
00363                 treeViewModel,
00364                 TerminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY],
00365                 Dispatcher,
00366                 () =>
00367                 {
00368                     CompilerServicesBackgroundTaskApi.DotNetSolution.SetDotNetSolution(treeViewSolution.Item.NamespacePath.AbsolutePath);
00369                     return Task.CompletedTask;
00370                 }),
00371             new MenuOptionRecord(
00372                 "Set as Startup Project",
00373                 MenuOptionKind.Other,
00374                 () =>
00375                 {
00376                     var startupControl = StartupControlStateWrap.Value.StartupControlList.FirstOrDefault(
00377                         x => x.StartupProjectAbsolutePath.Value == treeViewModel.Item.AbsolutePath.Value);
00378                         
00379                     if (startupControl is null)
00380                         return Task.CompletedTask;
00381                     
00382                     Dispatcher.Dispatch(new StartupControlState.SetActiveStartupControlKeyAction(startupControl.Key));  
00383                     return Task.CompletedTask;
00384                 }),
00385             DotNetMenuOptionsFactory.RemoveCSharpProjectReferenceFromSolution(
00386                 treeViewSolution,
00387                 treeViewModel,
00388                 TerminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY],
00389                 Dispatcher,
00390                 () =>
00391                 {
00392                     CompilerServicesBackgroundTaskApi.DotNetSolution.SetDotNetSolution(treeViewSolution.Item.NamespacePath.AbsolutePath);
00393                     return Task.CompletedTask;
00394                 }),
00395         };
00396     }
