00517     private void AddExistingProjectToSolution(DotNetSolutionModel dotNetSolutionModel)
00518     {
00519         IdeBackgroundTaskApi.InputFile.RequestInputFileStateForm(
00520             "Existing C# Project to add to solution",
00521             absolutePath =>
00522             {
00523                 if (absolutePath is null)
00524                     return Task.CompletedTask;
00525 
00526                 var localFormattedAddExistingProjectToSolutionCommand = DotNetCliCommandFormatter.FormatAddExistingProjectToSolution(
00527                     dotNetSolutionModel.NamespacePath.AbsolutePath.Value,
00528                     absolutePath.Value);
00529 
00530                 var terminalCommandRequest = new TerminalCommandRequest(
00531                     localFormattedAddExistingProjectToSolutionCommand.Value,
00532                     null)
00533                 {
00534                     ContinueWithFunc = parsedCommand =>
00535                     {
00536                         CompilerServicesBackgroundTaskApi.DotNetSolution.SetDotNetSolution(dotNetSolutionModel.NamespacePath.AbsolutePath);
00537                         return Task.CompletedTask;
00538                     }
00539                 };
00540                     
00541                 TerminalStateWrap.Value.TerminalMap[TerminalFacts.GENERAL_KEY].EnqueueCommand(terminalCommandRequest);
00542                 return Task.CompletedTask;
00543             },
00544             absolutePath =>
00545             {
00546                 if (absolutePath is null || absolutePath.IsDirectory)
00547                     return Task.FromResult(false);
00548 
00549                 return Task.FromResult(absolutePath.ExtensionNoPeriod.EndsWith(ExtensionNoPeriodFacts.C_SHARP_PROJECT));
00550             },
00551             new[]
00552             {
00553                 new InputFilePattern(
00554                     "C# Project",
00555                     absolutePath => absolutePath.ExtensionNoPeriod.EndsWith(ExtensionNoPeriodFacts.C_SHARP_PROJECT))
00556             }.ToImmutableArray());
00557     }
