00579     private async Task ReloadTreeViewModel(TreeViewNoType? treeViewModel)
00580     {
00581         if (treeViewModel is null)
00582             return;
00583 
00584         await treeViewModel.LoadChildListAsync().ConfigureAwait(false);
00585 
00586         TreeViewService.ReRenderNode(DotNetSolutionState.TreeViewSolutionExplorerStateKey, treeViewModel);
00587 
00588         TreeViewService.MoveUp(
00589             DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00590             false,
00591             false);
00592     }
