00051     public override Task OnKeyDownAsync(TreeViewCommandArgs commandArgs)
00052     {
00053         if (commandArgs.KeyboardEventArgs is null)
00054             return Task.CompletedTask;
00055 
00056         base.OnKeyDownAsync(commandArgs);
00057 
00058         switch (commandArgs.KeyboardEventArgs.Code)
00059         {
00060             case KeyboardKeyFacts.WhitespaceCodes.ENTER_CODE:
00061                 return InvokeOpenInEditor(commandArgs, true);
00062             case KeyboardKeyFacts.WhitespaceCodes.SPACE_CODE:
00063                 return InvokeOpenInEditor(commandArgs, false);
00064         }
00065 
00066         if (commandArgs.KeyboardEventArgs.CtrlKey)
00067         {
00068             CtrlModifiedKeymap(commandArgs);
00069             return Task.CompletedTask;
00070         }
00071         else if (commandArgs.KeyboardEventArgs.AltKey)
00072         {
00073             AltModifiedKeymap(commandArgs);
00074             return Task.CompletedTask;
00075         }
00076 
00077         return Task.CompletedTask;
00078     }
00079 
00080     private void CtrlModifiedKeymap(TreeViewCommandArgs commandArgs)
00081     {
00082         if (commandArgs.KeyboardEventArgs is null)
00083             return;
00084 
00085         if (commandArgs.KeyboardEventArgs.AltKey)
00086         {
00087             CtrlAltModifiedKeymap(commandArgs);
00088             return;
00089         }
00090 
00091         switch (commandArgs.KeyboardEventArgs.Key)
00092         {
00093             case "c":
00094                 InvokeCopyFile(commandArgs);
00095                 return;
00096             case "x":
00097                 InvokeCutFile(commandArgs);
00098                 return;
00099             case "v":
00100                 InvokePasteClipboard(commandArgs);
00101                 return;
00102         }
00103     }
00104 
00105     private void AltModifiedKeymap(TreeViewCommandArgs commandArgs)
00106     {
00107         return;
00108     }
00109 
00110     private void CtrlAltModifiedKeymap(TreeViewCommandArgs commandArgs)
00111     {
00112         return;
00113     }
