00115     private Task InvokeCopyFile(TreeViewCommandArgs commandArgs)
00116     {
00117         var activeNode = commandArgs.TreeViewContainer.ActiveNode;
00118 
00119         if (activeNode is not TreeViewNamespacePath treeViewNamespacePath)
00120             return Task.CompletedTask;
00121 
00122         var copyFileMenuOption = _menuOptionsFactory.CopyFile(
00123             treeViewNamespacePath.Item.AbsolutePath,
00124             () =>
00125             {
00126                 NotificationHelper.DispatchInformative("Copy Action", $"Copied: {treeViewNamespacePath.Item.AbsolutePath.NameWithExtension}", _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(7));
00127                 return Task.CompletedTask;
00128             });
00129 
00130         if (copyFileMenuOption.OnClickFunc is null)
00131             return Task.CompletedTask;
00132 
00133         return copyFileMenuOption.OnClickFunc.Invoke();
00134     }
00135 
00136     private Task InvokePasteClipboard(TreeViewCommandArgs commandArgs)
00137     {
00138         var activeNode = commandArgs.TreeViewContainer.ActiveNode;
00139 
00140         if (activeNode is not TreeViewNamespacePath treeViewNamespacePath)
00141             return Task.CompletedTask;
00142 
00143         MenuOptionRecord pasteMenuOptionRecord;
00144 
00145         if (treeViewNamespacePath.Item.AbsolutePath.IsDirectory)
00146         {
00147             pasteMenuOptionRecord = _menuOptionsFactory.PasteClipboard(
00148                 treeViewNamespacePath.Item.AbsolutePath,
00149                 async () =>
00150                 {
00151                     var localParentOfCutFile = SolutionExplorerContextMenu.ParentOfCutFile;
00152                     SolutionExplorerContextMenu.ParentOfCutFile = null;
00153 
00154                     if (localParentOfCutFile is not null)
00155                         await ReloadTreeViewModel(localParentOfCutFile).ConfigureAwait(false);
00156 
00157                     await ReloadTreeViewModel(treeViewNamespacePath).ConfigureAwait(false);
00158                 });
00159         }
00160         else
00161         {
00162             var parentDirectory = treeViewNamespacePath.Item.AbsolutePath.AncestorDirectoryList.Last();
00163             var parentDirectoryAbsolutePath = _environmentProvider.AbsolutePathFactory(parentDirectory.Value, true);
00164 
00165             pasteMenuOptionRecord = _menuOptionsFactory.PasteClipboard(
00166                 parentDirectoryAbsolutePath,
00167                 async () =>
00168                 {
00169                     var localParentOfCutFile = SolutionExplorerContextMenu.ParentOfCutFile;
00170                     SolutionExplorerContextMenu.ParentOfCutFile = null;
00171 
00172                     if (localParentOfCutFile is not null)
00173                         await ReloadTreeViewModel(localParentOfCutFile).ConfigureAwait(false);
00174 
00175                     await ReloadTreeViewModel(treeViewNamespacePath).ConfigureAwait(false);
00176                 });
00177         }
00178 
00179         if (pasteMenuOptionRecord.OnClickFunc is null)
00180             return Task.CompletedTask;
00181 
00182         return pasteMenuOptionRecord.OnClickFunc.Invoke();
00183     }
