00185     private Task InvokeCutFile(TreeViewCommandArgs commandArgs)
00186     {
00187         var activeNode = commandArgs.TreeViewContainer.ActiveNode;
00188 
00189         if (activeNode is not TreeViewNamespacePath treeViewNamespacePath)
00190             return Task.CompletedTask;
00191 
00192         var parent = treeViewNamespacePath.Parent as TreeViewNamespacePath;
00193 
00194         MenuOptionRecord cutFileOptionRecord = _menuOptionsFactory.CutFile(
00195             treeViewNamespacePath.Item.AbsolutePath,
00196             () =>
00197             {
00198                 NotificationHelper.DispatchInformative("Cut Action", $"Cut: {treeViewNamespacePath.Item.AbsolutePath.NameWithExtension}", _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(7));
00199                 SolutionExplorerContextMenu.ParentOfCutFile = parent;
00200                 return Task.CompletedTask;
00201             });
00202 
00203         if (cutFileOptionRecord.OnClickFunc is null)
00204             return Task.CompletedTask;
00205 
00206         return cutFileOptionRecord.OnClickFunc.Invoke();
00207     }
