00209     private Task InvokeOpenInEditor(TreeViewCommandArgs commandArgs, bool shouldSetFocusToEditor)
00210     {
00211         if (commandArgs.TreeViewContainer.ActiveNode is not TreeViewNamespacePath treeViewNamespacePath)
00212             return Task.CompletedTask;
00213             
00214         return _textEditorService.OpenInEditorAsync(
00215             treeViewNamespacePath.Item.AbsolutePath.Value,
00216             shouldSetFocusToEditor,
00217             null,
00218             new Category("main"),
00219             Key<TextEditorViewModel>.NewKey());
00220     }
