00222     private async Task ReloadTreeViewModel(TreeViewNoType? treeViewModel)
00223     {
00224         if (treeViewModel is null)
00225             return;
00226 
00227         await treeViewModel.LoadChildListAsync().ConfigureAwait(false);
00228 
00229         _treeViewService.ReRenderNode(
00230             DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00231             treeViewModel);
00232 
00233         _treeViewService.MoveUp(
00234             DotNetSolutionState.TreeViewSolutionExplorerStateKey,
00235             false,
00236             false);
00237     }
