00034     public MenuOptionRecord GetMenuOptionRecord(
00035         SolutionVisualizationModel localSolutionVisualizationModel,
00036         IEnvironmentProvider environmentProvider,
00037         LuthetusTextEditorConfig textEditorConfig,
00038         IServiceProvider serviceProvider)
00039     {
00040         var menuOptionRecordList = new List<MenuOptionRecord>();
00041 
00042         var targetDisplayName = "unknown";
00043 
00044         if (Item is ICompilerServiceResource compilerServiceResource)
00045         {
00046             var absolutePath = environmentProvider.AbsolutePathFactory(compilerServiceResource.ResourceUri.Value, false);
00047             targetDisplayName = absolutePath.NameWithExtension;
00048 
00049             menuOptionRecordList.Add(new MenuOptionRecord(
00050                 "Open in editor",
00051                 MenuOptionKind.Other,
00052                 OnClickFunc: () => OpenFileInEditor(
00053                     compilerServiceResource.ResourceUri.Value,
00054                     textEditorConfig,
00055                     serviceProvider)));
00056 
00057             if (SolutionVisualizationItemKind == SolutionVisualizationItemKind.Solution)
00058             {
00059                 menuOptionRecordList.Add(new MenuOptionRecord(
00060                     "Load Projects",
00061                     MenuOptionKind.Other,
00062                     OnClickFunc: () => LoadProjects(
00063                         (DotNetSolutionResource)compilerServiceResource,
00064                         textEditorConfig,
00065                         serviceProvider)));
00066             }
00067             else if (SolutionVisualizationItemKind == SolutionVisualizationItemKind.Project)
00068             {
00069                 menuOptionRecordList.Add(new MenuOptionRecord(
00070                     "Load Classes",
00071                     MenuOptionKind.Other,
00072                     OnClickFunc: () => LoadClasses(
00073                         (CSharpProjectResource)compilerServiceResource,
00074                         localSolutionVisualizationModel,
00075                         textEditorConfig,
00076                         serviceProvider)));
00077             }
00078         }
00079 
00080         return new MenuOptionRecord(
00081             targetDisplayName,
00082             MenuOptionKind.Other,
00083             SubMenu: new MenuRecord(menuOptionRecordList.ToImmutableArray()));
