00099     private async Task LoadProjects(
00100         DotNetSolutionResource dotNetSolutionResource,
00101         LuthetusTextEditorConfig textEditorConfig,
00102         IServiceProvider serviceProvider)
00103     {
00104         if (textEditorConfig.RegisterModelFunc is null)
00105             return;
00106 
00107         var dotNetSolutionStateWrap = serviceProvider.GetRequiredService<IState<DotNetSolutionState>>();
00108 
00109         var dotNetSolutionModel = dotNetSolutionStateWrap.Value.DotNetSolutionsList.FirstOrDefault(x =>
00110             x.AbsolutePath.Value == dotNetSolutionResource.ResourceUri.Value);
00111 
00112         foreach (var project in dotNetSolutionModel.DotNetProjectList)
00113         {
00114             var resourceUri = new ResourceUri(project.AbsolutePath.Value);
00115 
00116             await textEditorConfig.RegisterModelFunc.Invoke(new RegisterModelArgs(
00117                     resourceUri,
00118                     serviceProvider))
00119                 .ConfigureAwait(false);
00120         }
00121     }
