00123     private async Task LoadClasses(
00124         CSharpProjectResource cSharpProjectResource,
00125         SolutionVisualizationModel localSolutionVisualizationModel,
00126         LuthetusTextEditorConfig textEditorConfig,
00127         IServiceProvider serviceProvider)
00128     {
00129         if (textEditorConfig.RegisterModelFunc is null)
00130             return;
00131 
00132         var environmentProvider = serviceProvider.GetRequiredService<IEnvironmentProvider>();
00133         var fileSystemProvider = serviceProvider.GetRequiredService<IFileSystemProvider>();
00134         var commonComponentRenderers = serviceProvider.GetRequiredService<ICommonComponentRenderers>();
00135         var dispatcher = serviceProvider.GetRequiredService<IDispatcher>();
00136 
00137         var projectAbsolutePath = environmentProvider.AbsolutePathFactory(cSharpProjectResource.ResourceUri.Value, false);
00138 
00139         var parentDirectory = projectAbsolutePath.ParentDirectory;
00140         if (parentDirectory is null)
00141             return;
00142 
00143         var startingAbsolutePathForSearch = parentDirectory.Value;
00144 
00145         var discoveredFileList = new List<string>();
00146 
00147         await DiscoverFilesRecursively(startingAbsolutePathForSearch, discoveredFileList).ConfigureAwait(false);
00148 
00149         NotificationHelper.DispatchInformative(
00150             "SolutionVisualizationDrawing",
00151             $"{projectAbsolutePath.NameWithExtension} discovery {discoveredFileList.Count} C# files",
00152             commonComponentRenderers,
00153             dispatcher,
00154             TimeSpan.FromSeconds(6));
00155 
00156         foreach (var file in discoveredFileList)
00157         {
00158             if (!localSolutionVisualizationModel.ParentMap.TryAdd(file, this))
00159                 localSolutionVisualizationModel.ParentMap[file] = this;
00160         }
00161 
00162         foreach (var file in discoveredFileList)
00163         {
00164             var resourceUri = new ResourceUri(file);
00165 
00166             await textEditorConfig.RegisterModelFunc.Invoke(new RegisterModelArgs(
00167                     resourceUri,
00168                     serviceProvider))
00169                 .ConfigureAwait(false);
00170         }
