00172         async Task DiscoverFilesRecursively(string directoryPathParent, List<string> discoveredFileList)
00173         {
00174             var directoryPathChildList = await fileSystemProvider.Directory.GetDirectoriesAsync(
00175                     directoryPathParent,
00176                     CancellationToken.None)
00177                 .ConfigureAwait(false);
00178 
00179             var filePathChildList = await fileSystemProvider.Directory.GetFilesAsync(
00180                     directoryPathParent,
00181                     CancellationToken.None)
00182                 .ConfigureAwait(false);
00183 
00184             foreach (var filePathChild in filePathChildList)
00185             {
00186                 if (filePathChild.EndsWith(".cs"))
00187                     discoveredFileList.Add(filePathChild);
00188             }
00189 
00190             foreach (var directoryPathChild in directoryPathChildList)
00191             {
00192                 if (directoryPathChild.Contains(".git") ||
00193                     directoryPathChild.Contains(".vs") ||
00194                     directoryPathChild.Contains(".vscode") ||
00195                     directoryPathChild.Contains(".idea") ||
00196                     directoryPathChild.Contains("bin") ||
00197                     directoryPathChild.Contains("obj"))
00198                 {
00199                     continue;
00200                 }
00201 
00202                 await DiscoverFilesRecursively(directoryPathChild, discoveredFileList).ConfigureAwait(false);
00203             }
00204         }
00205     }
