00043     public SolutionVisualizationModel MakeDrawing(
00044         DotNetSolutionCompilerService dotNetSolutionCompilerService,
00045         CSharpProjectCompilerService cSharpProjectCompilerService,
00046         CSharpCompilerService cSharpCompilerService)
00047     {
00048         var localSolutionVisualizationModel = ShallowClone();
00049         localSolutionVisualizationModel.SolutionVisualizationDrawingList.Clear();
00050         localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList.Clear();
00051 
00052         // Do not clear the 'ParentMap', it will stop the connections from project to class.
00053         // localSolutionVisualizationModel.ParentMap.Clear();
00054 
00055         if (SolutionAbsolutePath is null)
00056             return localSolutionVisualizationModel;
00057 
00058         var dotNetSolutionResource = dotNetSolutionCompilerService.CompilerServiceResources.FirstOrDefault(
00059             x => x.ResourceUri.Value == SolutionAbsolutePath.Value);
00060 
00061         if (dotNetSolutionResource is null)
00062             return localSolutionVisualizationModel;
00063 
00064         var cSharpProjectResourceList = cSharpProjectCompilerService.CompilerServiceResources;
00065         var cSharpResourceList = cSharpCompilerService.CompilerServiceResources;
00066 
00067         var radius = 12;
00068         var centerX = 12;
00069         var centerY = 12;
00070         var rowIndex = 0;
00071         var columnIndex = 0;
00072         var renderCycleIndex = 0;
00073 
00074         // Regarding drawing the connections between the solution node, and its child project nodes.
00075         //
00076         // We need to draw the connections, before the nodes themselves, as to permit the connections be drawn
00077         // from the center of each node, rather than calculating the arc on the edge of the circle.
00078         //
00079         // i.e.: if the connections are drawn first, the node will perfectly cover the connection, curvature and all,
00080         //       even though the connection is simply a line from one point to another.
00081         //
00082         // That being said, we don't know the position of the nodes until their respective 'Draw...(...)' methods are invoked.
00083         // But, these draw methods don't actually render anything on the UI. It is just populating a list,
00084         // in which from index 0 to the end of that list, things will be rendered in that order.
00085         //
00086         // So, if at the end we insert the connections at index 0 of the "to be rendered list", it should work out.
00087         // An issue however, there are properties on the 'ISolutionVisualizationDrawing.cs' relating to the order that things were rendered.
00088         //
00089         // If we do not set these properties correctly, the C# code won't be able to properly determine what UI element was
00090         // clicked, (when two UI elements overlap, the most recently rendered element receives the click event).
00091         //
00092         // Therefore, an awkard incrementation of 'renderCycleIndex' is being done here. This leaves renderCycleIndex of index 0
00093         // free for use at a later time. renderCycleIndex of index 0 is saying, "this was part of the UI element group that was first rendered".
00094         //
00095         // Thus, if one clicks a node, the "covered connection" that extends through the perimiter of a node, to its center point.
00096         // This element won't get the click event.
00097         {
00098             // Reserve renderCycleIndex of 0 for the connections drawn between nodes.
00099             localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList.Add(new List<ISolutionVisualizationDrawing>());
00100             renderCycleIndex++;
00101         }
00102 
00103         DrawSolution(dotNetSolutionResource, localSolutionVisualizationModel, radius, centerX, centerY, rowIndex, columnIndex, renderCycleIndex);
00104         renderCycleIndex++;
00105         rowIndex++;
00106 
00107         DrawProjects(cSharpProjectResourceList, localSolutionVisualizationModel, radius, centerX, centerY, rowIndex, columnIndex, renderCycleIndex);
00108         renderCycleIndex++;
00109         rowIndex++;
00110 
00111         DrawClasses(cSharpResourceList, localSolutionVisualizationModel, radius, centerX, centerY, rowIndex, columnIndex, renderCycleIndex);
00112         renderCycleIndex++;
00113         rowIndex++;
00114 
00115         DrawConnections(localSolutionVisualizationModel, renderCycleIndex: 0);
00116 
00117         return localSolutionVisualizationModel;
00118     }
