00120     private void DrawSolution(
00121         ICompilerServiceResource dotNetSolutionResource,
00122         SolutionVisualizationModel localSolutionVisualizationModel,
00123         int radius,
00124         int centerX,
00125         int centerY,
00126         int rowIndex,
00127         int columnIndex,
00128         int renderCycleIndex)
00129     {
00130         centerX = localSolutionVisualizationModel.Dimensions.SvgWidth / 2 - radius / 2;
00131 
00132         localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList.Add(new List<ISolutionVisualizationDrawing>());
00133 
00134         var dotNetSolutionDrawing = new SolutionVisualizationDrawingCircle<DotNetSolutionResource>
00135         {
00136             Item = (DotNetSolutionResource)dotNetSolutionResource,
00137             SolutionVisualizationItemKind = SolutionVisualizationItemKind.Solution,
00138             CenterX = (1 + columnIndex) * centerX + columnIndex * radius + columnIndex * localSolutionVisualizationModel.Dimensions.HorizontalPadding,
00139             CenterY = (1 + rowIndex) * centerY + rowIndex * radius + rowIndex * localSolutionVisualizationModel.Dimensions.VerticalPadding,
00140             Radius = radius,
00141             Fill = "var(--luth_icon-solution-font-color)",
00142             RenderCycle = renderCycleIndex,
00143         };
00144 
00145         columnIndex++;
00146 
00147         localSolutionVisualizationModel.SolutionVisualizationDrawingList.Add(dotNetSolutionDrawing);
00148         localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[renderCycleIndex].Add(dotNetSolutionDrawing);
00149     }
