00151     private void DrawProjects(
00152         ImmutableArray<ICompilerServiceResource> cSharpProjectResourceList,
00153         SolutionVisualizationModel localSolutionVisualizationModel,
00154         int radius,
00155         int centerX,
00156         int centerY,
00157         int rowIndex,
00158         int columnIndex,
00159         int renderCycleIndex)
00160     {
00161         var solutionCenterX = localSolutionVisualizationModel.Dimensions.SvgWidth / 2 - radius / 2;
00162         var leftNodeCounter = 0;
00163         var rightNodeCounter = 0;
00164 
00165         localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList.Add(new List<ISolutionVisualizationDrawing>());
00166 
00167         for (int i = 0; i < cSharpProjectResourceList.Length; i++)
00168         {
00169             var cSharpProjectResource = cSharpProjectResourceList[i];
00170 
00171             if (i % 2 == 0)
00172             {
00173                 leftNodeCounter++;
00174                 var solutionRadius = radius;
00175                 var myRadius = leftNodeCounter * radius;
00176                 var previousCirclesRadius = (leftNodeCounter - 1) * radius;
00177                 var cumulativeHorizontalPadding = (leftNodeCounter - 1) * localSolutionVisualizationModel.Dimensions.HorizontalPadding;
00178                 centerX = solutionCenterX - solutionRadius - myRadius - previousCirclesRadius - cumulativeHorizontalPadding;
00179             }
00180             else
00181             {
00182                 rightNodeCounter++;
00183                 var solutionRadius = radius;
00184                 var myRadius = rightNodeCounter * radius;
00185                 var previousCirclesRadius = (rightNodeCounter - 1) * radius;
00186                 var cumulativeHorizontalPadding = (rightNodeCounter - 1) * localSolutionVisualizationModel.Dimensions.HorizontalPadding;
00187                 centerX = solutionCenterX + solutionRadius + myRadius + previousCirclesRadius + cumulativeHorizontalPadding;
00188             }
00189 
00190             var cSharpProjectDrawing = new SolutionVisualizationDrawingCircle<CSharpProjectResource>
00191             {
00192                 Item = (CSharpProjectResource)cSharpProjectResource,
00193                 SolutionVisualizationItemKind = SolutionVisualizationItemKind.Project,
00194                 CenterX = centerX,
00195                 CenterY = (1 + rowIndex) * centerY + rowIndex * radius + rowIndex * localSolutionVisualizationModel.Dimensions.VerticalPadding,
00196                 Radius = radius,
00197                 Fill = "var(--luth_icon-project-font-color)",
00198                 RenderCycle = renderCycleIndex,
00199             };
00200 
00201             columnIndex++;
00202 
00203             localSolutionVisualizationModel.SolutionVisualizationDrawingList.Add(cSharpProjectDrawing);
00204             localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[renderCycleIndex].Add(cSharpProjectDrawing);
00205         }
00206     }
