00227                 CenterY = (1 + rowIndex) * centerY + rowIndex * radius + rowIndex * localSolutionVisualizationModel.Dimensions.VerticalPadding,
00228                 Radius = radius,
00229                 Fill = "var(--luth_icon-c-sharp-class-font-color)",
00230                 RenderCycle = renderCycleIndex,
00231             };
00232 
00233             columnIndex++;
00234 
00235             localSolutionVisualizationModel.SolutionVisualizationDrawingList.Add(cSharpDrawing);
00236             localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[renderCycleIndex].Add(cSharpDrawing);
