00240     private void DrawConnections(
00241         SolutionVisualizationModel localSolutionVisualizationModel,
00242         int renderCycleIndex)
00243     {
00244         var solutionDrawing = localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[RENDER_CYCLE_LIST_SOLUTION_INDEX].Single();
00245         var projectDrawingList = localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[RENDER_CYCLE_LIST_PROJECTS_INDEX];
00246 
00247         // From solution to project connections
00248         foreach (var projectDrawing in projectDrawingList)
00249         {
00250             var solutionDrawingCircle = (ISolutionVisualizationDrawingCircle)solutionDrawing;
00251             var projectDrawingCircle = (ISolutionVisualizationDrawingCircle)projectDrawing;
00252 
00253             var connectionDrawing = new SolutionVisualizationDrawingLine<SolutionVisualizationConnection>
00254             {
00255                 Item = new SolutionVisualizationConnection(solutionDrawing, projectDrawing),
00256                 SolutionVisualizationItemKind = SolutionVisualizationItemKind.Connection,
00257                 StartPoint = (solutionDrawingCircle.CenterX, solutionDrawingCircle.CenterY),
00258                 EndPoint = (projectDrawingCircle.CenterX, projectDrawingCircle.CenterY),
00259                 Stroke = "var(--luth_primary-foreground-color)",
00260                 RenderCycle = renderCycleIndex,
00261             };
00262 
00263             localSolutionVisualizationModel.SolutionVisualizationDrawingList.Insert(0, connectionDrawing);
00264             localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[renderCycleIndex].Add(connectionDrawing);
00265         }
00266 
00267         var classDrawingList = localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[RENDER_CYCLE_LIST_CLASSES_INDEX];
00268 
00269         // From project to class connections
00270         foreach (var classDrawing in classDrawingList)
00271         {
00272             var cSharpResource = (CSharpResource)classDrawing.Item;
00273 
00274             Console.WriteLine($"localSolutionVisualizationModel.ParentMap::{localSolutionVisualizationModel.ParentMap.Count}");
00275 
00276             if (!localSolutionVisualizationModel.ParentMap.TryGetValue(cSharpResource.ResourceUri.Value, out var projectDrawing))
00277                 continue;
00278 
00279             var projectDrawingCircle = (ISolutionVisualizationDrawingCircle)projectDrawing;
00280             var classDrawingCircle = (ISolutionVisualizationDrawingCircle)classDrawing;
00281 
00282             var connectionDrawing = new SolutionVisualizationDrawingLine<SolutionVisualizationConnection>
00283             {
00284                 Item = new SolutionVisualizationConnection(projectDrawing, classDrawingCircle),
00285                 SolutionVisualizationItemKind = SolutionVisualizationItemKind.Connection,
00286                 StartPoint = (projectDrawingCircle.CenterX, projectDrawingCircle.CenterY),
00287                 EndPoint = (classDrawingCircle.CenterX, classDrawingCircle.CenterY),
00288                 Stroke = "var(--luth_primary-foreground-color)",
00289                 RenderCycle = renderCycleIndex,
00290             };
00291 
00292             localSolutionVisualizationModel.SolutionVisualizationDrawingList.Insert(0, connectionDrawing);
00293             localSolutionVisualizationModel.SolutionVisualizationDrawingRenderCycleList[renderCycleIndex].Add(connectionDrawing);
00294         }
00295     }
