00266     public (TextEditorTextSpan textSpan, string value) ReadWordTuple(ImmutableArray<char>? additionalCharactersToBreakOnList = null)
00267     {
00268         additionalCharactersToBreakOnList ??= ImmutableArray<char>.Empty;
00269 
00270         // The wordBuilder is appended to everytime a character is consumed.
00271         var wordBuilder = new StringBuilder();
00272 
00273         // wordBuilderStartingIndexInclusive == -1 is to mean that wordBuilder is empty.
00274         var wordBuilderStartingIndexInclusive = -1;
00275 
00276         while (!IsEof)
00277         {
00278             if (WhitespaceFacts.ALL_LIST.Contains(CurrentCharacter) ||
00279                 additionalCharactersToBreakOnList.Value.Contains(CurrentCharacter))
00280             {
00281                 break;
00282             }
00283 
00284             if (wordBuilderStartingIndexInclusive == -1)
00285             {
00286                 // This is the start of a word as opposed to the continuation of a word
00287                 wordBuilderStartingIndexInclusive = PositionIndex;
00288             }
00289 
00290             wordBuilder.Append(CurrentCharacter);
00291 
00292             _ = ReadCharacter();
00293         }
00294 
00295         return (new TextEditorTextSpan(
00296                 wordBuilderStartingIndexInclusive,
00297                 PositionIndex,
00298                 0,
00299                 ResourceUri,
00300                 SourceText),
00301             wordBuilder.ToString());
