00262     private void HandleOnKeyDown(KeyboardEventArgs keyboardEventArgs)
00263     {
00264         if (keyboardEventArgs.Key == ""Shift"" ||
00265             keyboardEventArgs.Key == ""Control"" ||
00266             keyboardEventArgs.Key == ""Alt"" ||
00267             keyboardEventArgs.Key == ""Meta"")
00268         {
00269             return;
00270         }
00271 
00272         if (keyboardEventArgs.CtrlKey && keyboardEventArgs.AltKey)
00273         {
00274             // TODO: This if is a hack to fix the keybind: { Ctrl + Alt + S } causing...
00275             // ...an 's' to be written out when using Vim keymap.
00276             return;
