00289             nameof(HandleOnKeyDown),
00290             async editContext =>
00291             {
00292                 var modelModifier = editContext.GetModelModifier(resourceUri);
00293                 var viewModelModifier = editContext.GetViewModelModifier(viewModelKey.Value);
00294 
00295                 if (modelModifier is null || viewModelModifier is null)
00296                     return;
00297 
00298                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00299                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00300 
00301                 if (primaryCursorModifier is null)
00302                     return;
00303 
00304                 var hasSelection = TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier);
00305                 var layerKey = ((ITextEditorKeymap)TextEditorService.OptionsStateWrap.Value.Options.Keymap!).GetLayer(hasSelection);
00306 
