00317                 {
00318                     _ = TextEditorService.OptionsStateWrap.Value.Options.Keymap!.Map.TryGetValue(
00319                         keymapArgument with
00320                         {
00321                             LayerKey = TextEditorKeymapDefaultFacts.DefaultLayer.Key,
