00323                         out command);
00324                 }
00325 
00326                 if (KeyboardKeyFacts.WhitespaceCodes.ENTER_CODE == keyboardEventArgs.Code && keyboardEventArgs.ShiftKey)
00327                     command = TextEditorCommandDefaultFacts.NewLineBelow;
