00430     private void HandleContentOnDoubleClick(MouseEventArgs mouseEventArgs)
00431     {
00432         var modelResourceUri = GetModel()?.ResourceUri;
00433         var viewModelKey = GetViewModel()?.ViewModelKey;
00434 
00435         if (modelResourceUri is null || viewModelKey is null)
00436             return;
00437 
00438         TextEditorService.Post(
00439             nameof(HandleContentOnDoubleClick),
00440             async editContext =>
00441             {
00442                 var modelModifier = editContext.GetModelModifier(modelResourceUri);
00443                 var viewModelModifier = editContext.GetViewModelModifier(viewModelKey.Value);
00444 
00445                 if (modelModifier is null || viewModelModifier is null)
00446                     return;
00447 
00448                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00449                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00450 
00451                 if (cursorModifierBag is null || primaryCursorModifier is null)
00452                     return;
00453 
00454                 var hasSelectedText = TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier);
00455 
00456                 if ((mouseEventArgs.Buttons & 1) != 1 && hasSelectedText)
00457                     return; // Not pressing the left mouse button so assume ContextMenu is desired result.
00458 
00459                 if (mouseEventArgs.ShiftKey)
00460                     return; // Do not expand selection if user is holding shift
00461 
00462                 var rowAndColumnIndex = await CalculateRowAndColumnIndex(mouseEventArgs).ConfigureAwait(false);
00463 
00464                 var lowerColumnIndexExpansion = modelModifier.GetColumnIndexOfCharacterWithDifferingKind(
00465                     rowAndColumnIndex.rowIndex,
00466                     rowAndColumnIndex.columnIndex,
00467                     true);
00468 
00469                 lowerColumnIndexExpansion = lowerColumnIndexExpansion == -1
00470                     ? 0
00471                     : lowerColumnIndexExpansion;
00472 
00473                 var higherColumnIndexExpansion = modelModifier.GetColumnIndexOfCharacterWithDifferingKind(
00474                     rowAndColumnIndex.rowIndex,
00475                     rowAndColumnIndex.columnIndex,
00476                     false);
00477 
00478                 higherColumnIndexExpansion = higherColumnIndexExpansion == -1
00479                         ? modelModifier.GetLengthOfRow(rowAndColumnIndex.rowIndex)
00480                         : higherColumnIndexExpansion;
00481 
00482                 // Move user's cursor position to the higher expansion
00483                 {
00484                     primaryCursorModifier.RowIndex = rowAndColumnIndex.rowIndex;
00485                     primaryCursorModifier.ColumnIndex = higherColumnIndexExpansion;
00486                     primaryCursorModifier.PreferredColumnIndex = rowAndColumnIndex.columnIndex;
00487                 }
00488 
00489                 // Set text selection ending to higher expansion
00490                 {
00491                     var cursorPositionOfHigherExpansion = modelModifier.GetPositionIndex(
00492                         rowAndColumnIndex.rowIndex,
00493                         higherColumnIndexExpansion);
00494 
00495                     primaryCursorModifier.SelectionEndingPositionIndex = cursorPositionOfHigherExpansion;
00496                 }
00497 
00498                 // Set text selection anchor to lower expansion
00499                 {
00500                     var cursorPositionOfLowerExpansion = modelModifier.GetPositionIndex(
00501                         rowAndColumnIndex.rowIndex,
00502                         lowerColumnIndexExpansion);
00503 
00504                     primaryCursorModifier.SelectionAnchorPositionIndex = cursorPositionOfLowerExpansion;
00505                 }
00506             });
00507     }
