00509     private void HandleContentOnMouseDown(MouseEventArgs mouseEventArgs)
00510     {
00511         var modelResourceUri = GetModel()?.ResourceUri;
00512         var viewModelKey = GetViewModel()?.ViewModelKey;
00513 
00514         if (modelResourceUri is null || viewModelKey is null)
00515             return;
00516         
00517         TextEditorService.Post(
00518             nameof(HandleContentOnMouseDown),
00519             async editContext =>
00520             {
00521                 var modelModifier = editContext.GetModelModifier(modelResourceUri);
00522                 var viewModelModifier = editContext.GetViewModelModifier(viewModelKey.Value);
00523 
00524                 if (modelModifier is null || viewModelModifier is null)
00525                     return;
00526 
00527                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00528                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00529 
00530                 if (cursorModifierBag is null || primaryCursorModifier is null)
00531                     return;
00532 
00533                 var hasSelectedText = TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier);
00534 
00535                 if ((mouseEventArgs.Buttons & 1) != 1 && hasSelectedText)
00536                     return; // Not pressing the left mouse button so assume ContextMenu is desired result.
00537 
00538                 CursorDisplay?.SetShouldDisplayMenuAsync(TextEditorMenuKind.None, false);
00539 
00540                 // Remember the current cursor position prior to doing anything
00541                 var inRowIndex = primaryCursorModifier.RowIndex;
00542                 var inColumnIndex = primaryCursorModifier.ColumnIndex;
00543 
00544                 // Move the cursor position
00545                 var rowAndColumnIndex = await CalculateRowAndColumnIndex(mouseEventArgs).ConfigureAwait(false);
00546                 primaryCursorModifier.RowIndex = rowAndColumnIndex.rowIndex;
00547                 primaryCursorModifier.ColumnIndex = rowAndColumnIndex.columnIndex;
00548                 primaryCursorModifier.PreferredColumnIndex = rowAndColumnIndex.columnIndex;
00549 
00550                 CursorDisplay?.PauseBlinkAnimation();
00551 
00552                 var cursorPositionIndex = modelModifier.GetPositionIndex(new TextEditorCursor(
00553                     rowAndColumnIndex.rowIndex,
00554                     rowAndColumnIndex.columnIndex,
00555                     true));
00556 
00557                 if (mouseEventArgs.ShiftKey)
00558                 {
00559                     if (!hasSelectedText)
00560                     {
00561                         // If user does not yet have a selection then place the text selection anchor were they were
00562                         primaryCursorModifier.SelectionAnchorPositionIndex = modelModifier
00563                             .GetPositionIndex(inRowIndex, inColumnIndex);
00564                     }
00565 
00566                     // If user ALREADY has a selection then do not modify the text selection anchor
00567                 }
00568                 else
00569                 {
00570                     primaryCursorModifier.SelectionAnchorPositionIndex = cursorPositionIndex;
00571                 }
00572 
00573                 primaryCursorModifier.SelectionEndingPositionIndex = cursorPositionIndex;
00574 
00575                 _thinksLeftMouseButtonIsDown = true;
00576             });
00577     }
