00580     private void HandleContentOnMouseMove(MouseEventArgs mouseEventArgs)
00581     {
00582         _userMouseIsInside = true;
00583         var localThinksLeftMouseButtonIsDown = _thinksLeftMouseButtonIsDown;
00584 
00585         // MouseStoppedMovingEvent
00586         {
00587             if (_tooltipViewModel is not null && _onMouseOutTooltipTask.IsCompleted)
00588             {
00589                 var onMouseOutTooltipCancellationToken = _onMouseOutTooltipCancellationTokenSource.Token;
00590 
00591                 _onMouseOutTooltipTask = Task.Run(async () =>
00592                 {
00593                     await Task.Delay(_onMouseOutTooltipDelay, onMouseOutTooltipCancellationToken).ConfigureAwait(false);
00594 
00595                     if (!onMouseOutTooltipCancellationToken.IsCancellationRequested)
00596                     {
00597                         _tooltipViewModel = null;
00598                         await InvokeAsync(StateHasChanged).ConfigureAwait(false);
00599                     }
00600                 });
00601             }
00602 
00603             _mouseStoppedMovingCancellationTokenSource.Cancel();
00604             _mouseStoppedMovingCancellationTokenSource = new();
00605 
00606             var cancellationToken = _mouseStoppedMovingCancellationTokenSource.Token;
00607 
00608             _mouseStoppedMovingTask = Task.Run(async () =>
00609             {
00610                 await Task.Delay(_mouseStoppedMovingDelay, cancellationToken).ConfigureAwait(false);
00611 
00612                 if (!cancellationToken.IsCancellationRequested && _userMouseIsInside)
00613                 {
00614                     await HandleOnTooltipMouseOverAsync().ConfigureAwait(false);
00615                     await HandleMouseStoppedMovingEventAsync(mouseEventArgs).ConfigureAwait(false);
00616                 }
00617             });
00618         }
00619 
00620         if (!_thinksLeftMouseButtonIsDown)
00621             return;
00622 
00623         var modelResourceUri = GetModel()?.ResourceUri;
00624         var viewModelKey = GetViewModel()?.ViewModelKey;
00625 
00626         if (modelResourceUri is null || viewModelKey is null)
00627             return;
00628 
00629         // Buttons is a bit flag '& 1' gets if left mouse button is held
00630         if (localThinksLeftMouseButtonIsDown && (mouseEventArgs.Buttons & 1) == 1)
00631         {
00632             TextEditorService.Post(
00633                 nameof(HandleContentOnMouseMove),
00634                 async editContext =>
00635                 {
00636                     var modelModifier = editContext.GetModelModifier(modelResourceUri);
00637                     var viewModelModifier = editContext.GetViewModelModifier(viewModelKey.Value);
00638 
00639                     if (modelModifier is null || viewModelModifier is null)
00640                         return;
00641 
00642                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00643                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00644 
00645                     if (cursorModifierBag is null || primaryCursorModifier is null)
00646                         return;
00647 
00648                     var rowAndColumnIndex = await CalculateRowAndColumnIndex(mouseEventArgs).ConfigureAwait(false);
00649 
00650                     primaryCursorModifier.RowIndex = rowAndColumnIndex.rowIndex;
00651                     primaryCursorModifier.ColumnIndex = rowAndColumnIndex.columnIndex;
00652                     primaryCursorModifier.PreferredColumnIndex = rowAndColumnIndex.columnIndex;
00653 
00654                     CursorDisplay?.PauseBlinkAnimation();
00655 
00656                     primaryCursorModifier.SelectionEndingPositionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00657                 });
00658         }
00659         else
00660         {
00661             _thinksLeftMouseButtonIsDown = false;
00662         }
00663     }
