00670     private async Task<(int rowIndex, int columnIndex)> CalculateRowAndColumnIndex(MouseEventArgs mouseEventArgs)
00671     {
00672         var model = GetModel();
00673         var viewModel = GetViewModel();
00674         var globalTextEditorOptions = TextEditorService.OptionsStateWrap.Value.Options;
00675 
00676         if (model is null || viewModel is null)
00677             return (0, 0);
00678 
00679         var charMeasurements = viewModel.VirtualizationResult.CharAndRowMeasurements;
00680 
00681         var relativeCoordinatesOnClick = await JsRuntime.InvokeAsync<RelativeCoordinates>(
00682                 ""luthetusTextEditor.getRelativePosition"",
00683                 viewModel.BodyElementId,
00684                 mouseEventArgs.ClientX,
00685                 mouseEventArgs.ClientY)
00686             .ConfigureAwait(false);
00687 
00688         var positionX = relativeCoordinatesOnClick.RelativeX;
00689         var positionY = relativeCoordinatesOnClick.RelativeY;
00690 
00691         // Scroll position offset
00692         {
00693             positionX += relativeCoordinatesOnClick.RelativeScrollLeft;
00694             positionY += relativeCoordinatesOnClick.RelativeScrollTop;
00695         }
00696 
00697         var rowIndex = (int)(positionY / charMeasurements.RowHeight);
00698 
00699         rowIndex = rowIndex > model.RowCount - 1
00700             ? model.RowCount - 1
00701             : rowIndex;
00702 
00703         int columnIndexInt;
00704 
00705         if (!globalTextEditorOptions.UseMonospaceOptimizations)
00706         {
00707             var guid = Guid.NewGuid();
00708 
00709             columnIndexInt = await JsRuntime.InvokeAsync<int>(
00710                     ""luthetusTextEditor.calculateProportionalColumnIndex"",
00711                     ProportionalFontMeasurementsContainerElementId,
00712                     $""luth_te_proportional-font-measurement-parent_{_textEditorHtmlElementId}_{guid}"",
00713                     $""luth_te_proportional-font-measurement-cursor_{_textEditorHtmlElementId}_{guid}"",
00714                     positionX,
00715                     charMeasurements.CharacterWidth,
00716                     model.GetLine(rowIndex))
00717                 .ConfigureAwait(false);
00718 
00719             if (columnIndexInt == -1)
00720             {
00721                 var columnIndexDouble = positionX / charMeasurements.CharacterWidth;
00722                 columnIndexInt = (int)Math.Round(columnIndexDouble, MidpointRounding.AwayFromZero);
00723             }
00724         }
00725         else
00726         {
00727             var columnIndexDouble = positionX / charMeasurements.CharacterWidth;
00728             columnIndexInt = (int)Math.Round(columnIndexDouble, MidpointRounding.AwayFromZero);
00729         }
00730 
00731         var lengthOfRow = model.GetLengthOfRow(rowIndex);
00732 
00733         // Tab key column offset
00734         {
00735             var parameterForGetTabsCountOnSameRowBeforeCursor = columnIndexInt > lengthOfRow
00736                 ? lengthOfRow
00737                 : columnIndexInt;
00738 
00739             var tabsOnSameRowBeforeCursor = model.GetTabsCountOnSameRowBeforeCursor(
00740                 rowIndex,
00741                 parameterForGetTabsCountOnSameRowBeforeCursor);
00742 
00743             // 1 of the character width is already accounted for
00744             var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00745 
00746             columnIndexInt -= extraWidthPerTabKey * tabsOnSameRowBeforeCursor;
00747         }
00748 
00749         columnIndexInt = columnIndexInt > lengthOfRow
00750             ? lengthOfRow
00751             : columnIndexInt;
00752 
00753         rowIndex = Math.Max(rowIndex, 0);
00754         columnIndexInt = Math.Max(columnIndexInt, 0);
00755 
00756         return (rowIndex, columnIndexInt);
00757     }
