00760     public TextEditorEdit HandleAfterOnKeyDownAsyncFactory(
00761         ResourceUri resourceUri,
00762         Key<TextEditorViewModel> viewModelKey,
00763         KeyboardEventArgs keyboardEventArgs,
00764         Func<TextEditorMenuKind, bool, Task> setTextEditorMenuKind)
00765     {
00766         return async editContext =>
00767         {
00768             var modelModifier = editContext.GetModelModifier(resourceUri);
00769             var viewModelModifier = editContext.GetViewModelModifier(viewModelKey);
00770 
00771             if (modelModifier is null || viewModelModifier is null)
00772                 return;
00773 
00774             var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00775             var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00776 
00777             if (primaryCursorModifier is null)
00778                 return;
00779 
00780             // Indexing can be invoked and this method still check for syntax highlighting and such
00781             if (IsAutocompleteIndexerInvoker(keyboardEventArgs))
00782             {
00783                 _ = Task.Run(async () => 
00784                 {
00785                     if (primaryCursorModifier.ColumnIndex > 0)
00786                     {
00787                         // All keyboardEventArgs that return true from ""IsAutocompleteIndexerInvoker""
00788                         // are to be 1 character long, as well either specific whitespace or punctuation.
00789                         // Therefore 1 character behind might be a word that can be indexed.
00790                         var word = modelModifier.ReadPreviousWordOrDefault(
00791                             primaryCursorModifier.RowIndex,
00792                             primaryCursorModifier.ColumnIndex);
00793 
00794                         if (word is not null)
00795                             await AutocompleteIndexer.IndexWordAsync(word).ConfigureAwait(false);
00796                     }
00797                 }).ConfigureAwait(false);
00798             }
00799 
00800             if (IsAutocompleteMenuInvoker(keyboardEventArgs))
00801             {
00802                 await setTextEditorMenuKind.Invoke(TextEditorMenuKind.AutoCompleteMenu, true).ConfigureAwait(false);
00803             }
00804             else if (IsSyntaxHighlightingInvoker(keyboardEventArgs))
00805             {
00806                 _throttleApplySyntaxHighlighting.FireAndForget(async _ =>
00807                 {
00808                     // The TextEditorModel may have been changed by the time this logic is ran and
00809                     // thus the local variables must be updated accordingly.
00810                     var model = GetModel();
00811                     var viewModel = GetViewModel();
00812 
00813                     if (model is not null)
00814                     {
00815                         await modelModifier.ApplySyntaxHighlightingAsync().ConfigureAwait(false);
00816 
00817                         if (viewModel is not null && model.CompilerService is not null)
00818                             model.CompilerService.ResourceWasModified(model.ResourceUri, ImmutableArray<TextEditorTextSpan>.Empty);
00819                     }
00820                 });
00821             }
00822         };
00823     }
