00825     private async Task HandleMouseStoppedMovingEventAsync(MouseEventArgs mouseEventArgs)
00826     {
00827         var model = GetModel();
00828         var viewModel = GetViewModel();
00829 
00830         if (model is null || viewModel is null)
00831             return;
00832 
00833         // Lazily calculate row and column index a second time. Otherwise one has to calculate it every mouse moved event.
00834         var rowAndColumnIndex = await CalculateRowAndColumnIndex(mouseEventArgs).ConfigureAwait(false);
00835 
00836         // TODO: (2023-05-28) This shouldn't be re-calcuated in the best case scenario. That is to say, the previous line invokes 'CalculateRowAndColumnIndex(...)' which also invokes this logic
00837         var relativeCoordinatesOnClick = await JsRuntime.InvokeAsync<RelativeCoordinates>(
00838                 ""luthetusTextEditor.getRelativePosition"",
00839                 viewModel.BodyElementId,
00840                 mouseEventArgs.ClientX,
00841                 mouseEventArgs.ClientY)
00842             .ConfigureAwait(false);
00843 
00844         var cursorPositionIndex = model.GetPositionIndex(new TextEditorCursor(
00845             rowAndColumnIndex.rowIndex,
00846             rowAndColumnIndex.columnIndex,
00847             true));
00848 
00849         var foundMatch = false;
00850 
00851         var symbols = model.CompilerService.GetSymbolsFor(model.ResourceUri);
00852         var diagnostics = model.CompilerService.GetDiagnosticsFor(model.ResourceUri);
00853 
00854         if (diagnostics.Length != 0)
00855         {
00856             foreach (var diagnostic in diagnostics)
00857             {
00858                 if (cursorPositionIndex >= diagnostic.TextSpan.StartingIndexInclusive &&
00859                     cursorPositionIndex < diagnostic.TextSpan.EndingIndexExclusive)
00860                 {
00861                     // Prefer showing a diagnostic over a symbol when both exist at the mouse location.
00862                     foundMatch = true;
00863 
00864                     var parameterMap = new Dictionary<string, object?>
00865                     {
00866                         {
00867                             nameof(ITextEditorDiagnosticRenderer.Diagnostic),
00868                             diagnostic
00869                         }
00870                     };
00871 
00872                     _tooltipViewModel = new(
00873                         LuthetusTextEditorComponentRenderers.DiagnosticRendererType,
00874                         parameterMap,
00875                         relativeCoordinatesOnClick,
00876                         null,
00877                         HandleOnTooltipMouseOverAsync);
00878                 }
00879             }
00880         }
00881 
00882         if (!foundMatch && symbols.Length != 0)
00883         {
00884             foreach (var symbol in symbols)
00885             {
00886                 if (cursorPositionIndex >= symbol.TextSpan.StartingIndexInclusive &&
00887                     cursorPositionIndex < symbol.TextSpan.EndingIndexExclusive)
00888                 {
00889                     foundMatch = true;
00890 
00891                     var parameters = new Dictionary<string, object?>
00892                     {
00893                         {
00894                             nameof(ITextEditorSymbolRenderer.Symbol),
00895                             symbol
00896                         }
00897                     };
00898 
00899                     _tooltipViewModel = new(
00900                         LuthetusTextEditorComponentRenderers.SymbolRendererType,
00901                         parameters,
00902                         relativeCoordinatesOnClick,
00903                         null,
00904                         HandleOnTooltipMouseOverAsync);
00905                 }
00906             }
00907         }
00908 
00909         if (!foundMatch)
00910         {
00911             if (_tooltipViewModel is null)
00912                 return; // Avoid the re-render if nothing changed
00913 
00914             _tooltipViewModel = null;
00915         }
00916 
00917         // TODO: Measure the tooltip, and reposition if it would go offscreen.
00918 
00919         await InvokeAsync(StateHasChanged).ConfigureAwait(false);
00920     }
