00922     private bool IsSyntaxHighlightingInvoker(KeyboardEventArgs keyboardEventArgs)
00923     {
00924         return keyboardEventArgs.Key == "";"" ||
00925                KeyboardKeyFacts.IsWhitespaceCode(keyboardEventArgs.Code) ||
00926                keyboardEventArgs.CtrlKey && keyboardEventArgs.Key == ""s"" ||
00927                keyboardEventArgs.CtrlKey && keyboardEventArgs.Key == ""v"" ||
00928                keyboardEventArgs.CtrlKey && keyboardEventArgs.Key == ""z"" ||
00929                keyboardEventArgs.CtrlKey && keyboardEventArgs.Key == ""y"";
00930     }
00931 
00932     private bool IsAutocompleteMenuInvoker(KeyboardEventArgs keyboardEventArgs)
00933     {
00934         // Is {Ctrl + Space} or LetterOrDigit was hit without Ctrl being held
00935         return keyboardEventArgs.CtrlKey && keyboardEventArgs.Code == KeyboardKeyFacts.WhitespaceCodes.SPACE_CODE ||
00936                !keyboardEventArgs.CtrlKey &&
00937                 !KeyboardKeyFacts.IsWhitespaceCode(keyboardEventArgs.Code) &&
00938                 !KeyboardKeyFacts.IsMetaKey(keyboardEventArgs);
00939     }
00940 
00946     private bool IsAutocompleteIndexerInvoker(KeyboardEventArgs keyboardEventArgs)
00947     {
00948         return (KeyboardKeyFacts.IsWhitespaceCode(keyboardEventArgs.Code) ||
00949                 KeyboardKeyFacts.IsPunctuationCharacter(keyboardEventArgs.Key.First())) &&
00950                !keyboardEventArgs.CtrlKey;
00951     }
