00996     private async Task HandleOnTouchMoveAsync(TouchEventArgs touchEventArgs)
00997     {
00998         var localThinksTouchIsOccurring = _thinksTouchIsOccurring;
00999 
01000         if (!_thinksTouchIsOccurring)
01001             return;
01002 
01003         var previousTouchPoint = _previousTouchEventArgs?.ChangedTouches.FirstOrDefault(x => x.Identifier == 0);
01004         var currentTouchPoint = touchEventArgs.ChangedTouches.FirstOrDefault(x => x.Identifier == 0);
01005 
01006         if (previousTouchPoint is null || currentTouchPoint is null)
01007             return;
01008 
01009         var viewModel = GetViewModel();
01010 
01011         if (viewModel is null)
01012             return;
01013 
01014         // Natural scrolling for touch devices
01015         var diffX = previousTouchPoint.ClientX - currentTouchPoint.ClientX;
01016         var diffY = previousTouchPoint.ClientY - currentTouchPoint.ClientY;
01017 
01018         viewModel.MutateScrollHorizontalPositionByPixels(diffX);
01019         viewModel.MutateScrollVerticalPositionByPixels(diffY);
01020 
01021         _previousTouchEventArgs = touchEventArgs;
01022     }
