01024     private void ClearTouch(TouchEventArgs touchEventArgs)
01025     {
01026         var rememberStartTouchEventArgs = _previousTouchEventArgs;
01027 
01028         _thinksTouchIsOccurring = false;
01029         _previousTouchEventArgs = null;
01030 
01031         var clearTouchDateTime = DateTime.UtcNow;
01032         var touchTimespan = clearTouchDateTime - _touchStartDateTime;
01033 
01034         if (touchTimespan is null)
01035             return;
01036 
01037         if (touchTimespan.Value.TotalMilliseconds < 200)
01038         {
01039             var startTouchPoint = rememberStartTouchEventArgs?.ChangedTouches.FirstOrDefault(x => x.Identifier == 0);
01040 
01041             if (startTouchPoint is null)
01042                 return;
01043 
01044             HandleContentOnMouseDown(new MouseEventArgs
01045             {
01046                 Buttons = 1,
01047                 ClientX = startTouchPoint.ClientX,
01048                 ClientY = startTouchPoint.ClientY,
01049             });
01050         }
01051     }
