00072     private async void DragStateWrapOnStateChanged(object? sender, EventArgs e)
00073     {
00074         if (IsBeingDragged)
00075             return;
00076 
00077         if (!DragStateWrap.Value.ShouldDisplay)
00078         {
00079             _dragEventHandler = null;
00080             _previousDragMouseEventArgs = null;
00081             _thinksLeftMouseButtonIsDown = false;
00082         }
00083         else
00084         {
00085             var mouseEventArgs = DragStateWrap.Value.MouseEventArgs;
00086 
00087             if (_dragEventHandler is not null)
00088             {
00089                 if (_previousDragMouseEventArgs is not null && mouseEventArgs is not null)
00090                 {
00091                     await _dragEventHandler
00092                         .Invoke((_previousDragMouseEventArgs, mouseEventArgs))
00093                         .ConfigureAwait(false);
00094                 }
00095 
00096                 _previousDragMouseEventArgs = mouseEventArgs;
00097                 await InvokeAsync(StateHasChanged);
00098             }
00099         }
00100     }
