00128     private async Task HandleOnMouseDownAsync(MouseEventArgs mouseEventArgs)
00129     {
00130         if (IsBeingDragged)
00131             return;
00132 
00133         if (mouseEventArgs.Button == 0)
00134             _thinksLeftMouseButtonIsDown = true;
00135         if (mouseEventArgs.Button == 1)
00136             await CloseTabOnClickAsync().ConfigureAwait(false);
00137         else if (mouseEventArgs.Button == 2)
00138             ManuallyPropagateOnContextMenu(mouseEventArgs, Tab);
00139     }
