00141     private void ManuallyPropagateOnContextMenu(
00142         MouseEventArgs mouseEventArgs,
00143         ITab tab)
00144     {
00145         var localHandleTabButtonOnContextMenu = HandleTabButtonOnContextMenu;
00146 
00147         if (localHandleTabButtonOnContextMenu is null)
00148             return;
00149 
00150         BackgroundTaskService.Enqueue(
00151             Key<IBackgroundTask>.NewKey(),
00152             ContinuousBackgroundTaskWorker.GetQueueKey(),
00153             "Tab.ManuallyPropagateOnContextMenu",
00154             async () => await localHandleTabButtonOnContextMenu
00155                 .Invoke(new TabContextMenuEventArgs(mouseEventArgs, tab, FocusAsync))
00156                 .ConfigureAwait(false));
00157     }
