00159     private async Task FocusAsync()
00160     {
00161         try
00162         {
00163             var localTabButtonElementReference = _tabButtonElementReference;
00164 
00165             if (localTabButtonElementReference is not null)
00166             {
00167                 await localTabButtonElementReference.Value
00168                     .FocusAsync()
00169                     .ConfigureAwait(false);
00170             }
00171         }
00172         catch (Exception)
00173         {
00174             // 2023-04-18: The app has had a bug where it "freezes" and must be restarted.
00175             //             This bug is seemingly happening randomly. I have a suspicion
00176             //             that there are race-condition exceptions occurring with "FocusAsync"
00177             //             on an ElementReference.
00178         }
00179     }
